use kunskapsskolan;

DROP TABLE IF EXISTS ks_user_bookmarks;
DROP TABLE IF EXISTS ks_user_subjects;
DROP TABLE IF EXISTS ks_user_strategies;
DROP TABLE IF EXISTS ks_ratings;
DROP TABLE IF EXISTS ks_subjects_tags;
DROP TABLE IF EXISTS ks_subjects;
DROP TABLE IF EXISTS ks_users;
DROP TABLE IF EXISTS ks_schools;
DROP TABLE IF EXISTS ks_strategies_tags;
DROP TABLE IF EXISTS ks_strategies;

CREATE TABLE ks_strategies (
	id INT NOT NULL AUTO_INCREMENT,
	title VARCHAR(255) NOT NULL,
category VARCHAR(255) NOT NULL,
	description MEDIUMTEXT NOT NULL,
	expert_rated VARCHAR(255),
	PRIMARY KEY (id)
);

CREATE TABLE ks_strategies_tags (
	id INT NOT NULL AUTO_INCREMENT,
	tag VARCHAR(255) NOT NULL,
	strategy_id INT NOT NULL,
FOREIGN KEY (strategy_id) REFERENCES ks_strategies(id),
	PRIMARY KEY (id)
);

CREATE TABLE ks_schools (
	id INT NOT NULL AUTO_INCREMENT,
	name VARCHAR(255) NOT NULL,
	municipality VARCHAR(255),
	city VARCHAR(255),
     	country VARCHAR(255),
	PRIMARY KEY (id)
);

CREATE TABLE ks_users (
	id INT NOT NULL AUTO_INCREMENT,
	#type VARCHAR(255) NOT NULL,
	school_id int NOT NULL,
	FOREIGN KEY (school_id) REFERENCES ks_schools(id),
	PRIMARY KEY (id)
);

CREATE TABLE ks_subjects (
	id INT NOT NULL AUTO_INCREMENT,
subject VARCHAR(255) NOT NULL,
block int NOT NULL,
	PRIMARY KEY (id)
);

CREATE TABLE ks_subjects_tags (
	id INT NOT NULL AUTO_INCREMENT,
	tag VARCHAR(255) NOT NULL,
	subject_id  int NOT NULL,
FOREIGN KEY (subject_id) REFERENCES ks_subjects(id),
	PRIMARY KEY (id)
);

CREATE TABLE ks_ratings (
	id INT NOT NULL AUTO_INCREMENT,
	rating INT NOT NULL,
	user_id int NOT NULL,
strategy_id int NOT NULL,
subject_id int NOT NULL,
FOREIGN KEY (user_id) REFERENCES ks_users(id),
	FOREIGN KEY (strategy_id) REFERENCES ks_strategies(id),
FOREIGN KEY (subject_id) REFERENCES ks_subjects(id),
	PRIMARY KEY (id)
);

CREATE TABLE ks_user_strategies (
	id INT NOT NULL AUTO_INCREMENT,
	user_id int NOT NULL,
strategy_id int NOT NULL,
subject_id int NOT NULL,
FOREIGN KEY (subject_id) REFERENCES ks_subjects(id),
	FOREIGN KEY (user_id) REFERENCES ks_users(id),
FOREIGN KEY (strategy_id) REFERENCES ks_strategies(id),
	PRIMARY KEY (id)
);

CREATE TABLE ks_user_subjects (
	id INT NOT NULL AUTO_INCREMENT,
	user_id int NOT NULL,
subject_id  int NOT NULL,
	FOREIGN KEY (user_id) REFERENCES ks_users(id),
FOREIGN KEY (subject_id) REFERENCES ks_subjects(id),
	PRIMARY KEY (id)
);

CREATE TABLE ks_user_bookmarks (
	id INT NOT NULL AUTO_INCREMENT,
	user_id int NOT NULL,
strategy_id  int NOT NULL,
	FOREIGN KEY (user_id) REFERENCES ks_users(id),
FOREIGN KEY (strategy_id) REFERENCES ks_strategies(id),
	PRIMARY KEY (id)
);



VALUES
('Att få respons','Analysera och reflektera','<p class="c7"><span class="c3">Responsen &auml;r ett verktyg f&ouml;r att hj&auml;lpa dig fram&aring;t f&ouml;r att analysera och utveckla din text. N&auml;r du bearbetar din text s&aring; reflektera &auml;ven &ouml;ver den kamratrespons som du kanske nyligen gett, finns det n&aring;got i den som inspirerade dig?</span></p><p class="c1"><span class="c3"></span></p><p class="c1"><span class="c3"></span></p><p class="c7"><span class="c3">Bild/filmruta/ljudfil</span></p><p class="c1"><span class="c3"></span></p><p class="c1"><span class="c3"></span></p><p class="c7"><span class="c4 c10">N&auml;r du f&aring;r respons p&aring; din text</span></p><ul class="c6 lst-kix_hgba5c1drdqq-0 start"><li class="c5"><span class="c0">Kom ih&aring;g att den text som du f&aring;tt respons p&aring; inte &auml;r den f&auml;rdiga produkten, utan bara ett utkast som ska bearbetas. Po&auml;ngen med kamratrespons &auml;r att du ska f&aring; hj&auml;lp med att bearbeta din text.</span></li><li class="c5"><span class="c0">L&auml;s, reflektera och analysera den respons du f&aring;tt.</span></li><li class="c5"><span class="c0">Undvik att f&ouml;rsvara din text. Om du tycker att de synpunkter du f&aring;tt &auml;r oviktiga s&aring; v&auml;ljer du sj&auml;lv om du vill ta med dem eller inte, det &auml;r du som &auml;ger din text.</span></li><li class="c5"><span class="c0">T&auml;nk p&aring; att din text ska fungera utan att du m&aring;ste f&ouml;rklara hur du t&auml;nkt eller hur man ska uppfatta det du har skrivit. Bearbeta ist&auml;llet texten s&aring; att den fungerar utan kompletterande muntliga f&ouml;rklaringar.</span></li><li class="c5"><span class="c4">Fr&aring;ga g&auml;rna kamraterna om r&aring;d. &rdquo;F&ouml;rst&aring;r ni det jag skrivit h&auml;r? Hur uppfattar ni det h&auml;r partiet? Vad f&aring;r ni f&ouml;r k&auml;nsla av inledningen? Hur ska jag g&ouml;ra f&ouml;r att f&aring; det h&auml;r avsnittet lite fylligare?</span></li></ul>'),
('Att ge kamratrespons','Analysera och reflektera','<p class="c3"><span class="c0">Att ge respons och kritik p&aring; n&aring;gon annans text &auml;r l&auml;ttare &auml;n att analysera sin egen. N&auml;r du granskar en kamrats arbete kommer du samtidigt att reflektera kring och f&aring; m&ouml;jlighet till inspiration till utveckling av ditt egna arbete. </span></p><p class="c2"><span class="c0"></span></p><p class="c2"><span class="c0"></span></p><p class="c3"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c2"><span class="c0"></span></p><p class="c2"><span class="c0"></span></p><p class="c3"><span class="c7">Innan kamratresponsen</span></p><ul class="c1 lst-kix_k0521y5ge7zt-0 start"><li class="c3 c4"><span class="c0">Dela dokument med varandra och utg&aring; ifr&aring;n en bed&ouml;mningsmatris om det finns. Finns ingen uppgiftsspecifik matris s&aring; anv&auml;nd beskrivningen av uppgiften. </span></li><li class="c3 c4"><span class="c0">&Auml;r det t ex en kr&ouml;nika, reportage eller artikel s&aring; finns det oftast en skrivmall som man kan utg&aring; ifr&aring;n.</span></li></ul><p class="c2 c6"><span class="c0"></span></p><p class="c3"><span class="c7">Under kamratresponsen</span></p><ul class="c1 lst-kix_gilddjk3d0m4-0 start"><li class="c3 c4"><span class="c0">T&auml;nk positivt och var konstruktiv.</span></li><li class="c3 c4"><span class="c0">Var &auml;rlig, men lyh&ouml;rd och f&ouml;rsiktig.</span></li><li class="c3 c4"><span class="c0">F&ouml;rs&ouml;k att vara s&aring; konkret som m&ouml;jligt, motivera och ge exempel.</span></li><li class="c3 c4"><span class="c0">Gl&ouml;m inte att huvudsyftet &auml;r att den du ger respons p&aring; ska k&auml;nna sig inspirerad och utveckla sin text vidare.</span></li><li class="c3 c4"><span class="c0">Ber&auml;tta hur du uppfattar texten (syftet, temat, po&auml;ngen).</span></li><li class="c3 c4"><span class="c0">Peka p&aring; s&aring;dant du inte riktigt f&ouml;rst&aring;r och som du skulle vilja ha mer utvecklat. Formulera g&auml;rna fr&aring;gor som f&auml;ster skribentens uppm&auml;rksamhet p&aring; svaga punkter i texten. </span></li><li class="c3 c4"><span class="c0">Ge g&auml;rna f&ouml;rslag p&aring; alternativa rubriker (kopplat till det du uppfattar som textens huvudsyfte och huvudtanke).</span></li></ul><p class="c2 c6"><span class="c0"></span></p><p class="c3"><span class="c8">Efter kamratresponsen</span><span>&nbsp;&auml;r ett eget dokument i strategibanken som heter</span><span class="c7">&nbsp;&ldquo;Att f&aring; respons&rdquo;</span></p><p class="c2"><span class="c7"></span></p><p class="c3"><span class="c7">Obs! Fastna inte i sm&aring; spr&aring;kliga detaljer, f&ouml;rs&ouml;k att se helheten! T&auml;nk ocks&aring; p&aring; hur du s&auml;ger n&aring;got, din ton &auml;r viktigt n&auml;r du ger respons.</span></p><p class="c2"><span class="c0"></span></p>'),
('Reflektera över matematiska lösningar','Analysera och reflektera','<p class="c1"><span class="c2">N&auml;r man l&ouml;ser problem i matematik s&aring; redovisar man oftast sina l&ouml;sningar med b&aring;de text och siffror. Men att skriva ner matematiska l&ouml;sningar som ber&auml;ttelser h&ouml;r inte till vanligheterna. Denna strategi ger dig en metod att ensam eller tillsammans med en studiekompis reflektera och f&aring; en b&auml;ttre f&ouml;rst&aring;else f&ouml;r dina matematiska l&ouml;sningar.</span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1 c3"><span class="c2"></span></p><ul class="c5 lst-kix_lw7f2wow5j20-0 start"><li class="c0"><span class="c2">V&auml;lj ett matematiskt problem som du/ni var f&ouml;r sig l&ouml;st eller l&ouml;ser.</span></li><li class="c0"><span class="c2">B&ouml;rja d&auml;refter med att beskriva problemet med egna skrivna ord. G&ouml;r problemet till en ber&auml;ttelse.</span></li><li class="c0"><span class="c2">D&auml;refter skriver du ner hur du har l&ouml;st uppgiften och reflekterar &ouml;ver varf&ouml;r du valt att g&ouml;ra de utr&auml;kningar som du gjort.</span></li><li class="c0"><span class="c2">Avsluta din ber&auml;ttelse med att motivera om svaret &auml;r realistiskt eller ej.</span></li><li class="c0"><span class="c2">Nu ska du och din studiekompis j&auml;mf&ouml;ra era beskrivningar. </span></li></ul><ul class="c5 lst-kix_lw7f2wow5j20-1 start"><li class="c1 c4"><span class="c2">Reflektera &ouml;ver olikheter och likheter. </span></li><li class="c1 c4"><span class="c2">Vilken metod var enklast att f&ouml;rst&aring;?</span></li></ul>'),
('Självobservation','Analysera och reflektera','<p class="c9"><span class="c1">Hur g&ouml;r jag? Vad g&ouml;r jag? Hur kan jag g&ouml;ra detta p&aring; ett annat s&auml;tt? Vad h&auml;nder n&auml;r jag g&ouml;r p&aring; detta s&auml;tt? Vad beh&ouml;ver jag utveckla? Genom att g&ouml;ra en sj&auml;lvobservation s&aring; reflekterar du &ouml;ver ditt l&auml;rande.</span></p><p class="c3"><span class="c1"></span></p><p class="c3"><span class="c1"></span></p><p class="c9"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c3"><span class="c1"></span></p><p class="c3"><span class="c1"></span></p><p class="c9"><span class="c1">B&ouml;rja med att svara ja eller nej p&aring; fr&aring;gorna (g&ouml;r g&auml;rna en kopia av detta dokument f&ouml;r att kunna fylla i f&auml;lten nedan)</span></p><ol class="c5 lst-kix_lwv7eumrxl9q-0 start" start="1"><li class="c9 c12"><span class="c1">Jag har blivit b&auml;ttre p&aring; n&aring;got jag redan kan/vet </span></li><li class="c9 c12"><span class="c1">Jag har l&auml;rt mig n&aring;got nytt/f&aring;tt nya id&eacute;er</span></li></ol><p class="c3"><span class="c1"></span></p><a id="t.005a4d866873602ae9f56ae3e60a54980da5fd7f"></a><a id="t.0"></a><table class="c7"><tbody><tr class="c11"><td class="c10" colspan="1" rowspan="1"><p class="c9"><span class="c1">Om ja &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Vad har jag l&auml;rt mig? (kan vara flera saker, beskriv konkret!)</span></p><p class="c3"><span class="c1"></span></p><ol class="c5 lst-kix_t8aahi4v4hri-0 start" start="1"><li class="c9 c12"><span class="c1">&nbsp;</span></li></ol><p class="c3"><span class="c1"></span></p></td></tr></tbody></table><p class="c3"><span class="c1"></span></p><a id="t.34abf356fa14d44f9549fffbb22dd23b80843bb3"></a><a id="t.1"></a><table class="c7"><tbody><tr class="c11"><td class="c10" colspan="1" rowspan="1"><p class="c2"><span class="c1">Om ja &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Hur l&auml;rde jag mig? (vad hj&auml;lpte mig/underl&auml;ttade l&auml;randet?)</span></p><p class="c2 c8"><span class="c1"></span></p><ol class="c5 lst-kix_kw6mux4fhssm-0 start" start="1"><li class="c0"><span class="c1">&nbsp;</span></li></ol><p class="c2 c8"><span class="c1"></span></p></td></tr></tbody></table><p class="c3"><span class="c1"></span></p><a id="t.def6c41b59b397a5b586bd0d61b81b26f225b05a"></a><a id="t.2"></a><table class="c7"><tbody><tr class="c11"><td class="c10" colspan="1" rowspan="1"><p class="c9"><span class="c1">Om ja &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Fanns det &auml;nd&aring; hinder/faktorer som f&ouml;rsv&aring;rade l&auml;randet, i s&aring; fall vad?</span></p><p class="c3"><span class="c1"></span></p><ol class="c5 lst-kix_q75iit7jbrg9-0 start" start="1"><li class="c9 c12"><span class="c1">&nbsp;</span></li></ol><p class="c3"><span class="c1"></span></p></td></tr></tbody></table><p class="c3"><span class="c1"></span></p><a id="t.8da38e696ba9ee13e6cad2de458514927e6c3b01"></a><a id="t.3"></a><table class="c7"><tbody><tr class="c11"><td class="c10" colspan="1" rowspan="1"><p class="c2"><span class="c1">Om nej &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Jag har inte l&auml;rt mig n&aring;gonting, varf&ouml;r? (Hinder, mm)</span></p>'),
('Bra appar för bättre fokus','Appar och tillägg','<p class="c0"><span class="c1"></span></p><p class="c3"><span class="c5 c6">Forest</span></p><p class="c3"><span>Om du har sv&aring;rt att anv&auml;nder telefon kan du till exempel ta hj&auml;lp av appen </span><span class="c5">Forest. </span><span class="c1">Den hj&auml;lper dig att plantera ett tr&auml;d om du lyckas h&aring;lla dig ifr&aring;n att pilla p&aring; telefonen. N&auml;r du v&auml;l lyckats f&aring; ihop till en hel skog s&aring; planterar skaparen av appen ett tr&auml;d p&aring; riktigt.</span></p><p class="c0"><span class="c1"></span></p><p class="c3"><span class="c6 c5">Pomodoro</span></p><p class="c3"><span class="c1">En tomatapp som hj&auml;lper dig att fokusera. Pomodoro betyder tomat och innefattar en metod med sj&auml;lvreflektion. Appen &auml;r en timer som du s&auml;tter p&aring; 25 min och d&auml;refter tar du paus p&aring; 5 min. N&auml;r du uppn&aring;tt 4 tomater f&aring;r du en l&auml;ngre paus.</span></p>'),
('Belöningar','Hitta motivationen','<p class="c3"><span class="c7">Att bel&ouml;na sig sj&auml;lv kan vara ett bra s&auml;tt att h&aring;lla sig motiverad eller till och med &ouml;ka motivationen. </span></p><p class="c3 c4"><span class="c0"></span></p><p class="c3"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c3 c4"><span class="c0"></span></p><ol class="c1 lst-kix_xndn6276sode-0 start" start="1"><li class="c2"><span class="c0">G&ouml;r en lista &ouml;ver bel&ouml;ningar som du tror skulle motivera dig att arbeta med dina uppgifter (fika, titta p&aring; tv, spela tv-spel, rita en stund etc).</span></li><li class="c2"><span class="c0">Ha dem till hands n&auml;r du ska planera din vecka eller ditt arbetspass. </span></li><li class="c2"><span class="c0">Best&auml;m att du f&aring;r en bel&ouml;ning efter att du tex har arbetat i 30 minuter eller har r&auml;knat klart 10 uppgifter.</span></li><li class="c2"><span class="c0">Bel&ouml;na dig sj&auml;lv n&auml;r du har uppn&aring;tt m&aring;let du satte. </span></li><li class="c2"><span class="c0">K&auml;nn efter. K&auml;nn &ldquo;S&aring; grym jag &auml;r!&rdquo;</span></li></ol><p class="c3 c4"><span class="c0"></span></p>'),
('Gör en sak i taget','Hitta motivationen','<p class="c0"><span class="c1">Har du sv&aring;rt att komma ig&aring;ng och f&aring; saker gjorda?</span></p><p class="c0"><span class="c1">G&auml;ller det framf&ouml;r allt n&auml;r du har uppgifter fr&aring;n skolan?</span></p><p class="c0"><span class="c1">Best&auml;m i f&ouml;rv&auml;g mellan vilka tider du ska plugga.</span></p><p class="c0 c2"><span class="c1"></span></p><p class="c0 c2"><span class="c1"></span></p><p class="c0 c2"><span class="c1"></span></p><p class="c0"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c0 c2"><span class="c1"></span></p><p class="c0"><span class="c1">Instruktion f&ouml;r strategin</span></p><ol class="c3 lst-kix_8xzd9rcu727x-0 start" start="1"><li class="c0 c4"><span class="c1">T&auml;nk aldrig n&aring;gonsin att du ska sitta med ett skolarbete &ldquo;resten av kv&auml;llen&rdquo; eller &ldquo;hela s&ouml;ndagen&rdquo;. En risk &auml;r d&aring; att du drar dig f&ouml;r att s&auml;tta ig&aring;ng, eftersom du sedan &auml;r fast i ett l&aring;ngt o&ouml;versk&aring;dligt arbete. </span></li><li class="c0 c4"><span class="c1">Best&auml;m ist&auml;llet mellan vilka tider arbetet ska ske och g&ouml;r en uppskattning &ouml;ver hur l&aring;ng tid du beh&ouml;ver till ditt f&ouml;rfogande.</span></li><li class="c0 c4"><span class="c1">N&auml;r du v&auml;l har best&auml;mt mellan vilka tider du ska studera &auml;r det viktigt att ta bort alla distraktioner. S&aring; bort med mobil, st&auml;ng ner sidor p&aring; datorn du inte har anv&auml;ndning f&ouml;r och be lillebror att h&aring;lla sig borta.</span></li>'),
('Hitta motivationen','Hitta motivationen','<p class="c1"><span class="c0">Motivation &auml;r alltid individuell.</span></p><p class="c1"><span class="c0">Du som elev beh&ouml;ver fundera &ouml;ver vad som fungerar b&auml;st f&ouml;r dig. En bra strategi som dock fungerar f&ouml;r m&aring;nga &auml;r att s&auml;tta tydliga, utmanande och rimliga m&aring;l.</span></p><p class="c1 c5"><span class="c0"></span></p><p class="c1 c5"><span class="c0"></span></p><p class="c1"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1 c5"><span class="c0"></span></p><p class="c1"><span class="c0">Instruktion f&ouml;r strategin</span></p><p class="c1 c5"><span class="c0"></span></p><p class="c1 c5"><span class="c0"></span></p><ol class="c8 lst-kix_q18s0bflrks9-0 start" start="1"><li class="c1 c4"><span class="c0">S&auml;tt m&aring;l! Det &auml;r bra att ha en h&ouml;gre m&aring;ls&auml;ttning n&auml;r du studerar, b&aring;de vad g&auml;ller kortsiktiga och l&aring;ngsiktiga m&aring;l.</span></li><li class="c1 c4"><span class="c0">Om du s&auml;tter upp m&aring;l i b&ouml;rjan av ett arbetsomr&aring;de kommer du m&auml;rka att det &auml;r l&auml;ttare att uppn&aring; vissa studieresultat. Du kommer &auml;ven k&auml;nna tillfredsst&auml;llelse n&auml;r du bockar av m&aring;l p&aring; v&auml;gen, eller uppn&aring;r det slutliga resultatet.</span></li><li class="c1 c4"><span class="c0">Det &auml;r en bra id&eacute; att s&auml;tta upp mindre m&aring;l som &auml;r relativt enkla. </span></li><li class="c1 c4"><span class="c0">Ge inte upp n&auml;r du st&auml;lls inf&ouml;r en utmaning p&aring; v&auml;gen - fundera ist&auml;llet p&aring; hur den kan &ouml;vervinnas.N&auml;r du lyckas med att &ouml;vervinna ett hinder kommer du f&aring; h&ouml;gre sj&auml;lvf&ouml;rtroende och kan m&ouml;ta andra utmaningar.</span></li><li class="c1 c4"><span class="c0">Ibland kan ett moment av studierna eller kursen k&auml;nnas alltf&ouml;r sv&aring;rt. Det &auml;r viktigt att inte ge upp utan att slutf&ouml;ra uppgiften, med hj&auml;lp av en l&auml;rare eller kompis, och sedan skapa nya utmaningar.</span></li>'),
('Bli bättre på något','Hitta motivationen','<p class="c3"><span class="c1">Motivation &auml;r drivkraften att vilja utvecklas. Alla vill vi utvecklas men &auml;nd&aring; s&aring; brister motivationen ibland. Du kanske k&auml;nner dig omotiverad, oengagerad, k&auml;nner att uppgiften &auml;r meningsl&ouml;s eller obegriplig. D&aring; &auml;r det viktigt att ha bra strategier f&ouml;r att f&ouml;rs&ouml;ka hitta motivationen.</span></p><p class="c2"><span class="c1"></span></p><p class="c3"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c2"><span class="c1"></span></p><ol class="c5 lst-kix_k3su74vw70wy-0 start" start="1"><li class="c3 c4"><span class="c1">S&auml;tt fingret p&aring; vad du vill bli b&auml;ttre p&aring;.</span></li><li class="c3 c4"><span class="c1">S&auml;tt upp sm&aring; delm&aring;l. </span></li><li class="c3 c4"><span class="c1">Fira n&auml;r du uppn&aring;tt delm&aring;len, bel&ouml;na dig.</span></li><li class="c3 c4"><span class="c1">S&auml;tt upp nytt m&aring;l som du kan arbeta mot. </span></li><li class="c3 c4"><span class="c1">Ge inte upp n&auml;r du st&auml;lls inf&ouml;r en utmaning p&aring; v&auml;gen - fundera ist&auml;llet p&aring; hur den kan &ouml;vervinnas. </span></li><li class="c3 c4"><span class="c1">M&aring;nga sm&aring; framg&aring;ngar, s&aring; v&auml;xer motivationen att forts&auml;tta &auml;ven n&auml;r det blir sv&aring;rt.</span></li>'),
('Gör det bara','Hitta motivationen','<p class="c3"><span class="c6">&rdquo;Jag kan inte&rdquo; eller &rdquo;jag orkar inte&rdquo; handlar oftast om brist p&aring; motivation och blir l&auml;tt till en urs&auml;kt f&ouml;r att inte ens f&ouml;rs&ouml;ka. T&auml;nk p&aring; att allt du g&ouml;r inte m&aring;ste blir perfekt utan att ett f&ouml;rs&ouml;k &auml;r b&auml;ttre &auml;n att inte g&ouml;ra n&aring;got alls. </span></p><p class="c0"><span class="c1"></span></p><p class="c3"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c0"><span class="c1"></span></p><ol class="c4 lst-kix_37xnevs7cym4-0 start" start="1"><li class="c3 c5"><span class="c1">S&auml;tt dig in i vad det &auml;r du ska g&ouml;ra.</span></li><li class="c3 c5"><span class="c1">G&ouml;r en lista &ouml;ver vad som m&aring;ste g&ouml;ras.</span></li><li class="c3 c5"><span class="c1">F&ouml;rs&ouml;k hitta n&aring;got i uppgiften som kan intressera dig. </span></li><li class="c3 c5"><span class="c1">P&aring;b&ouml;rja uppgiften. </span></li><li class="c3 c5"><span class="c1">Fastnar du be kamrat, familjemedlem eller l&auml;raren om hj&auml;lp.</span></li></ol>'),
('Gör uppgiften till din','Hitta motivationen','<p class="c6"><span class="c1">Motivation &auml;r drivkraften att vilja utvecklas. Alla vill vi utvecklas men &auml;nd&aring; s&aring; brister motivationen ibland. Du kanske k&auml;nner dig omotiverad, oengagerad, k&auml;nner att uppgiften &auml;r meningsl&ouml;s eller obegriplig. D&aring; &auml;r det viktigt att ha bra strategier f&ouml;r att f&ouml;rs&ouml;ka hitta motivationen.</span></p><p class="c2"><span class="c1"></span></p><p class="c6"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c2"><span class="c1"></span></p><ol class="c8 lst-kix_bt6sjjp46puc-0 start" start="1"><li class="c0"><span class="c1">F&ouml;rs&ouml;k hitta n&aring;got i uppgiften som intresserar dig eller som du anser vara viktigt. </span></li><li class="c0"><span class="c1">G&ouml;r uppgiften till din genom att b&ouml;rja med att kolla upp det du tycker &auml;r intressant eller viktigt. </span></li><li class="c0"><span class="c1">Ge inte upp n&auml;r du st&auml;lls inf&ouml;r en utmaning p&aring; v&auml;gen - fundera ist&auml;llet p&aring; hur den kan &ouml;vervinnas. Kommer du inte p&aring; l&ouml;sningen be en kompis eller l&auml;rare om hj&auml;lp. </span></li></ol>'),
('30minutersmetoden','Hitta studiefokus','<p class="c2"><span class="c1">Har du sv&aring;rt att motivera dig att plugga under en l&auml;ngre period? Eller har du s&aring; mycket att g&ouml;ra att du inte vet var du ska b&ouml;rja? Testa den h&auml;r metoden f&ouml;r att g&ouml;ra arbetet mer hanterbart, och du kommer troligtvis bli f&ouml;rv&aring;nad &ouml;ver hur mycket du hinner med.</span></p><ol class="c7 lst-kix_6h17maatpkpz-0 start" start="1"><li class="c2 c3"><span class="c1">Best&auml;m en uppgift, eller ett &auml;mne, som du ska jobba med. </span></li><li class="c2 c3"><span>S&auml;tt en timer p&aring; 30 minuter.</span><span>&nbsp;Du kan anv&auml;nda din mobil </span><span class="c11"><a class="c12" </span></li><li class="c2 c3"><span class="c1">N&auml;r 30 minuter har g&aring;tt s&aring; sl&auml;pper du arbetet och g&ouml;r n&aring;got annat i fem-tio minuter.</span></li></ol><p class="c2 c4"><span class="c1"></span></p><p class="c2"><span class="c1">&Auml;r du inte klar med det du skulle f&aring; gjort kan du boka in 30 nya minuter d&aring; du kan forts&auml;tta. Troligen kommer du under de 30-minuterspassen vara mer fokuserad och f&aring; mer gjort &auml;n du vad h1trodde.</span></p>'),
('Att undvika att bli störd','Hitta studiefokus','<p class="c1"><span class="c2">Ofta beh&ouml;ver man jobba p&aring; med skolarbetet. Det &auml;r l&auml;tt att bli distraherad, st&ouml;rd av kompisar, mobil och annat. H&auml;r &auml;r n&aring;gra tips som du kan pr&ouml;va. </span></p><p class="c1"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c1 c4"><span class="c2"></span></p><p class="c1"><span class="c2">Instruktion f&ouml;r strategin</span></p><p class="c1 c4"><span class="c2"></span></p><p class="c1 c4"><span class="c2"></span></p><p class="c1 c4"><span class="c2"></span></p><ol class="c6 lst-kix_bm3zsgr5as1h-0 start" start="1"><li class="c1 c3"><span class="c2">Ber&auml;tta f&ouml;r andra att du &auml;r upptagen - till exempel s&auml;g att du beh&ouml;ver plugga i 40 minuter men att du g&auml;rna ses p&aring; lunchen. </span></li><li class="c1 c3"><span class="c2">Markera att du inte vill bli st&ouml;rd genom att ha skolarbetet tydligt framme. H&ouml;rlurar kan fungera bra, &auml;ven om du inte lyssnar p&aring; n&aring;got. Du verkar upptagen. </span></li><li class="c1 c3"><span class="c2">St&auml;ng av alla on&ouml;diga elektroniska apparater och/eller program som kan distrahera dig. </span></li><li class="c1 c3"><span class="c2">S&auml;tt upp ett arbetsm&aring;l f&ouml;r att beh&aring;lla koncentrationen, tex&rdquo; p&aring; denna ws ska jag r&auml;kna 10 tal&rdquo;, eller &ldquo;p&aring; denna ws ska jag skriva en disposition till min ber&auml;ttelse&rdquo;</span></li><li class="c1 c3"><span class="c2">Skapa en bra inl&auml;rningsmilj&ouml; med st&auml;dat skrivbord.</span></li><li class="c1 c3"><span class="c2">Stoppa undan alla f&ouml;rem&aring;l du inte beh&ouml;ver under studietiden</span></li></ol>'),
('Byt format','Hitta studiefokus','<p class="c2"><span class="c1">En van l&auml;sare kan l&auml;sa l&aring;nga avsnitt utan st&ouml;rre anstr&auml;ngning och tankarna kan l&auml;tt b&ouml;rja vandra iv&auml;g. D&aring; &auml;r det bra att ha en strategi som hj&auml;lper dig att t&auml;nka p&aring; vad du l&auml;ser medan du l&auml;ser det.</span></p><p class="c0"><span class="c1"></span></p><p class="c2"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c0"><span class="c1"></span></p><p class="c2"><span class="c1">Instruktion f&ouml;r strategin</span></p><p class="c0"><span class="c1"></span></p><p class="c0"><span class="c1"></span></p><p class="c0"><span class="c1"></span></p><ol class="c4 lst-kix_f24pbedy3e65-0 start" start="1"><li class="c2 c7"><span class="c1">Ibland &auml;r det n&ouml;dv&auml;ndigt att l&auml;sa en text flera g&aring;nger, men oftast vill man bara f&aring; ut s&aring; mycket som m&ouml;jligt av en text vid en enda genoml&auml;sning.</span></li></ol><p class="c0 c9"><span class="c1"></span></p><ol class="c4 lst-kix_f24pbedy3e65-0" start="2"><li class="c2 c7"><span class="c1">Ist&auml;llet f&ouml;r att ta anteckningar, notera fetstilta och kursiva ord, kan du ist&auml;llet ber&auml;tta med r&ouml;sten.</span></li><li class="c2 c7"><span class="c1">Om du ber&auml;ttar med r&ouml;sten vad du just l&auml;st s&aring; m&aring;ste du formulera en sammanfattning sj&auml;lv utan att l&auml;sa innantill. D&aring; byter du format fr&aring;n text till tal och det tvingar dig att t&auml;nka.</span></li></ol><p class="c0"><span class="c1"></span></p>'),
('Håll fokus och skärp hela dagen','Hitta studiefokus','<p class="c3"><span class="c0">Hur h&aring;ller du koncentrationen uppe?</span></p><p class="c3"><span class="c0">Pomodoro eller som det heter p&aring; svenska tomatmetoden hj&auml;lper dig att arbeta effektivt och kan till och med korta ner din pluggtid. Den skapades av italienaren Francesco Cirillo 80 talet. </span></p><p class="c1"><span class="c0"></span></p><p class="c3"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1"><span class="c0"></span></p><p class="c3"><span class="c0">Instruktion f&ouml;r strategin</span></p><p class="c1"><span class="c0"></span></p><p class="c1"><span class="c0"></span></p><ol class="c9 lst-kix_wqy7ggw76lbo-0 start" start="1"><li class="c2"><span class="c0">Best&auml;m vilken uppgift du ska g&ouml;ra.</span></li><li class="c2"><span class="c0">Fundera p&aring; &nbsp;hur l&aring;ng tid den tar. En pomodoro &auml;r 25 minuter.</span></li><li class="c2"><span class="c0">Ta bort alla distraktioner. L&auml;gg bort mobilen, st&auml;ng ner alla andra sidor p&aring; datorn. S&auml;tt klockan p&aring; 25 minuter.</span></li><li class="c2"><span class="c0">Under dessa 25 minuter arbetar du bara med din uppgift. </span></li><li class="c2"><span class="c0">Ta 5 minuters paus och anv&auml;nd den p&aring; ett vettigt s&auml;tt. Drick n&aring;got, &auml;t en frukt, str&auml;ck p&aring; dig.</span></li><li class="c2"><span class="c0">Efter din paus b&ouml;rjar du med att anteckna i 3-4 minuter &nbsp;vad du l&auml;ste eller l&auml;rde dig innan pausen.</span></li><li class="c2"><span class="c0">S&auml;tt klockan p&aring; 25 minuter igen. Forts&auml;tt arbeta fokuserat.</span></li><li class="c2"><span class="c0">Ta 5 minuters paus.</span></li><li class="c2"><span class="c0">Viktigt. Om du b&ouml;rjar t&auml;nka p&aring; n&aring;got och blir distraherad, skriv ner det och forts&auml;tt sedan med din uppgift. Ta inte itu med dem f&ouml;rr&auml;n du &auml;r helt klar med det du ska.</span></li><li class="c2"><span class="c0">N&auml;r du gjort 4 tomater &auml;r det dags f&ouml;r l&auml;ngre paus.</span></li><li class="c2"><span class="c0">N&auml;r du pr&ouml;vat pomodorometoden n&aring;gra g&aring;nger kan du titta p&aring; dina anteckningar. Har m&aring;nga tankar dykt upp under tiden som du arbetat s&aring; kan du kanske se ett m&ouml;nster i n&auml;r du tappar koncentrationen och n&auml;r den fungerar. </span></li></ol>'),
('Kontrollera dina tankar','Hitta studiefokus','<p class="c7"><span>Att l&auml;gga energi p&aring; att f&ouml;r&auml;ndra saker som man inte kan styra &ouml;ver &auml;r att l&auml;gga tid p&aring; fel saker. </span><span class="c9">D&aring; &auml;r det mycket b&auml;ttre att l&auml;gga sin tid och sina tankar p&aring; s&aring;dant man kan f&ouml;r&auml;ndra och p&aring;verka.</span><span class="c3">&nbsp;</span></p><p class="c1"><span class="c3"></span></p><p class="c1"><span class="c3"></span></p><p class="c7"><span class="c3">Bild/filmruta/ljudfil</span></p><p class="c1"><span class="c3"></span></p><p class="c7"><span class="c3">Instruktion f&ouml;r strategin</span></p><p class="c1"><span class="c3"></span></p><p class="c1"><span class="c3"></span></p><ol class="c11 lst-kix_hjzfovkcgbf0-0 start" start="1"><li class="c7 c10"><span class="c3">Exempel p&aring; s&aring;dant som du kan kontrollera &auml;r: </span></li></ol><ol class="c11 lst-kix_hjzfovkcgbf0-1 start" start="1"><li class="c2"><span class="c3">Din egen attityd till saker.</span></li><li class="c2"><span class="c3">Att s&auml;tta upp m&aring;l.</span></li><li class="c2"><span class="c3">Att hitta strategier f&ouml;r att jobba mot m&aring;len.</span></li><li class="c2"><span class="c3">Att hitta metoder f&ouml;r att genomf&ouml;ra din strategi.</span></li><li class="c2"><span class="c3">Att helt enkelt ge dig sj&auml;lv r&auml;tt f&ouml;ruts&auml;ttningar</span></li></ol><p class="c1 c12"><span class="c3"></span></p><ol class="c11 lst-kix_hjzfovkcgbf0-0" start="2"><li class="c7 c10"><span class="c3">Du kan kanske inte p&aring;verka att folk pratar runt omkring dig. Men du kan p&aring;verka var du s&auml;tter dig att arbeta i ett rum och kanske v&auml;lja att &nbsp;anv&auml;nda h&ouml;rlurar. &nbsp;</span></li><li class="c7 c10"><span class="c3">Du kan inte p&aring;verka vilka betygskriterier som finns. Men du kan best&auml;mma dig f&ouml;r att f&ouml;rs&ouml;ka f&ouml;rst&aring; dem och arbeta mot det resultat du vill uppn&aring;.</span></li></ol><p class="c1 c12"><span class="c3"></span></p><ol class="c11 lst-kix_hjzfovkcgbf0-0" start="4"><li class="c7 c10"><span class="c3">G&ouml;r s&aring; h&auml;r:</span></li></ol><ol class="c11 lst-kix_hjzfovkcgbf0-1 start" start="1"><li class="c2"><span class="c3">Ta n&aring;got du tycker &auml;r sv&aring;rt och skapa en tabell p&aring; vad du kan p&aring;verka i den saken f&ouml;r att n&aring; dina m&aring;l. Det skulle kunna se ut s&aring; h&auml;r:</span></li></ol><p class="c1 c12"><span class="c3"></span></p><p class="c7"><span class="c5">Jag har sv&aring;rt att komma i tid till lektionen fast jag &auml;r i skolan.</span></p><p class="c1"><span class="c3"></span></p><p class="c1"><span class="c3"></span></p><a id="t.b3114bb55323d140cb11935b084ca2ef035ed4ca"></a><a id="t.0"></a><table class="c13"><tbody><tr class="c6"><td class="c0" colspan="1" rowspan="1"><p class="c8"><span class="c5">Saker jag kan ha kontroll &ouml;ver:</span></p></td><td class="c0" colspan="1" rowspan="1"><p class="c8"><span class="c5">Saker jag inte kan ha kontroll &ouml;ver:</span></p></td></tr><tr class="c6"><td class="c0" colspan="1" rowspan="1"><p class="c8"><span class="c3">Att i god tid titta vad klockan &auml;r. Dubbelkontrollera i vilket rum lektionen &auml;r och r&auml;kna ut hur l&aring;ng tid det tar att g&aring; dit.</span></p><p class="c8"><span class="c3">Se till att vara d&auml;r n&aring;gon minut innan det b&ouml;rjar s&aring; att du i lugn och ro kan v&auml;lja plats som passar dina behov.</span></p></td><td class="c0" colspan="1" rowspan="1"><p class="c8"><span class="c3">N&auml;r lektionen b&ouml;rjar. </span></p>'),
('Lär dig prioritera','Hitta studiefokus','<p class="c5"><span class="c1">Har du f&ouml;r mycket att g&ouml;ra p&aring; samma g&aring;ng? Flera inl&auml;mningar, prov, tr&auml;ning och l&auml;xor som g&ouml;r dig stressad? D&aring; &auml;r det bra att kunna prioritera bland alla uppgifter.</span></p><p class="c4"><span class="c1"></span></p><p class="c5"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c4"><span class="c1"></span></p><ol class="c0 lst-kix_h9cd69hzlqmp-0 start" start="1"><li class="c2"><span class="c1">G&ouml;r en lista. </span></li><li class="c2"><span class="c1">Rangordna i din lista. Vad &auml;r viktigast att b&ouml;rja med?</span></li><li class="c2"><span class="c1">Har du stora uppgifter - dela upp dem i mindre projekt.</span></li><li class="c2"><span class="c1">Uppskatta hur l&aring;ng tid varje uppgift kommer att ta. De uppgifter som tar l&auml;ngre tid delas upp med pauser emellan. </span></li><li class="c2"><span class="c1">Ta tag i f&ouml;rsta uppgiften och t&auml;nk inte p&aring; det som kommer h&auml;rn&auml;st.</span></li><li class="c2"><span class="c1">G&ouml;r en sak i taget.</span></li><li class="c2"><span class="c1">Ha din prioritetslista n&auml;ra till hands (strategirutan i loggboken, i mobilen, p&aring; v&auml;ggen hemma etc)</span></li>'),
('Läs ett stycke i taget','Hitta studiefokus','<p class="c3"><span class="c1">Att kunna l&auml;sa effektivt &auml;r en viktig och v&auml;ldigt anv&auml;ndbar strategi.</span></p><p class="c3"><span class="c1">H&auml;r kommer tips p&aring; hur du kan g&ouml;ra.</span></p><p class="c3 c5"><span class="c1"></span></p><p class="c3"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c3 c5"><span class="c1"></span></p><p class="c3"><span class="c1">Instruktion f&ouml;r strategin</span></p><p class="c3 c5"><span class="c1"></span></p><p class="c3 c5"><span class="c1"></span></p><ol class="c6 lst-kix_7evxsoqtflck-0 start" start="1"><li class="c2"><span class="c1">Prova att bara l&auml;sa ett stycke i taget ur en text. </span></li><li class="c2"><span class="c1">Stanna upp, l&auml;gg bort texten, ber&auml;tta vad du har l&auml;st. </span></li><li class="c2"><span class="c1">Kan du inte formulera dig, eller k&auml;nner du dig okoncentrerad? L&auml;s om stycket.</span></li><li class="c2"><span class="c1">Det &auml;r b&auml;ttre att stanna upp ofta &nbsp;&auml;n att l&auml;sa flera sidor innan du uppt&auml;cker att du t&auml;nkt p&aring; n&aring;got annat. </span></li><li class="c2"><span class="c1">Forts&auml;tt med n&auml;sta stycke. L&auml;gg bort texten. Ber&auml;tta vad du l&auml;st. </span></li><li class="c2"><span class="c1">Forts&auml;tt tills du l&auml;st och ber&auml;ttat om hela texten.</span></li></ol>'),
('Spela in','Hitta studiefokus','<p class="c3"><span class="c1">Att kunna l&auml;sa effektivt &auml;r en viktig och v&auml;ldigt anv&auml;ndbar strategi.</span></p><p class="c3"><span class="c1">H&auml;r kommer tips p&aring; hur du kan g&ouml;ra.</span></p><p class="c3 c5"><span class="c1"></span></p><p class="c3"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c3 c5"><span class="c1"></span></p><p class="c3"><span class="c1">Instruktion f&ouml;r strategin</span></p><p class="c3 c5"><span class="c1"></span></p><p class="c3 c5"><span class="c1"></span></p><ol class="c6 lst-kix_7evxsoqtflck-0 start" start="1"><li class="c2"><span class="c1">Prova att bara l&auml;sa ett stycke i taget ur en text. </span></li><li class="c2"><span class="c1">Stanna upp, l&auml;gg bort texten, ber&auml;tta vad du har l&auml;st. </span></li><li class="c2"><span class="c1">Kan du inte formulera dig, eller k&auml;nner du dig okoncentrerad? L&auml;s om stycket.</span></li><li class="c2"><span class="c1">Det &auml;r b&auml;ttre att stanna upp ofta &nbsp;&auml;n att l&auml;sa flera sidor innan du uppt&auml;cker att du t&auml;nkt p&aring; n&aring;got annat. </span></li><li class="c2"><span class="c1">Forts&auml;tt med n&auml;sta stycke. L&auml;gg bort texten. Ber&auml;tta vad du l&auml;st. </span></li><li class="c2"><span class="c1">Forts&auml;tt tills du l&auml;st och ber&auml;ttat om hela texten.</span></li></ol>'),
('Studiefokus hemma','Hitta studiefokus','<p class="c6"><span class="c0">Ibland beh&ouml;ver man plugga hemma med skolarbetet. Det &auml;r l&auml;tt att bli distraherad, st&ouml;rd av syskon, mobil och annat. H&auml;r &auml;r n&aring;gra tips som du kan pr&ouml;va.</span></p><p class="c1"><span class="c0"></span></p><p class="c6"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1"><span class="c0"></span></p><p class="c6"><span class="c0">Instruktion f&ouml;r strategin</span></p><p class="c1"><span class="c0"></span></p><ol class="c3 lst-kix_fsl494gexj4m-0 start" start="1"><li class="c2"><span class="c0">Ber&auml;tta f&ouml;r familjen att du beh&ouml;ver vara i fred. </span></li><li class="c2"><span class="c0">Hitta ett st&auml;lle d&auml;r du kan plugga utan distraktioner.</span></li><li class="c2"><span class="c0">Se till att ha allt till hands innan du s&auml;tter ig&aring;ng.</span></li><li class="c2"><span class="c0">Ta regelbundna pauser.</span></li><li class="c2"><span class="c0">N&auml;r du tappar fokus reflektera &ouml;ver varf&ouml;r.</span></li><li class="c2"><span class="c0">P&aring;minn dig om varf&ouml;r du pluggar.</span></li></ol>'),
('Studiefokus i workshop','Hitta studiefokus','<p class="c10"><span class="c0">Det &auml;r l&auml;tt att bli distraherad och st&ouml;rd av kompisar och allt som sker runt om. H&auml;r &auml;r n&aring;gra tips som du kan anv&auml;nda f&ouml;r att hitta studiero i work shop. </span></p><p class="c3"><span class="c0"></span></p><p class="c3"><span class="c0"></span></p><ol class="c2 lst-kix_68jmazpo1ppa-0 start" start="1"><li class="c4"><span class="c0">S&auml;tt upp arbetsm&aring;l t.ex. &ldquo;p&aring; denna workshop ska jag r&auml;kna 10 tal&rdquo; eller &ldquo;p&aring; denna workshop ska jag rita upp en tankekarta om organisk kemi&rdquo;. </span></li><li class="c4"><span class="c0">S&auml;tt dig p&aring; en plats s&aring; att du f&aring;r mycket gjort. &nbsp;</span></li></ol><p class="c9"><span class="c0">Beh&ouml;ver du mycket hj&auml;lp av l&auml;raren just den h&auml;r workshopen se till att sitta s&aring; att l&auml;raren ser dig, beh&ouml;ver du sitta i tyst rum s&aring; g&aring;r du dit.</span></p><ol class="c2 lst-kix_68jmazpo1ppa-0" start="3"><li class="c4"><span class="c0">Se till att ha en arml&auml;ngds avst&aring;nd till klasskamrater s&aring; du f&aring;r din egen plats i rummet f&ouml;r ett b&auml;ttre fokus.</span></li><li class="c4"><span class="c0">St&auml;ng av alla on&ouml;diga elektroniska apparater, program och appar som kan distrahera dig. </span></li><li class="c4"><span class="c0">Stoppa undan alla f&ouml;rem&aring;l du inte beh&ouml;ver under studietiden.</span></li></ol>'),
('Stå upp','Hitta studiefokus','<p class="c1"><span class="c3">Om du sitter i mer &auml;n 30 minuter g&aring;r hj&auml;rnans &ldquo;sk&auml;rmsl&auml;ckare&rdquo; p&aring;. Det beror p&aring; att din hj&auml;rna fungerar fungerar p&aring; liknande s&auml;tt som den m&auml;nskliga hj&auml;rnan gjort i 400000 &aring;r.</span></p><p class="c1"><span class="c3">N&auml;r dina f&ouml;rf&auml;der satt i 30 minuter innebar det oftast att de vilade. De kunde slappna av, st&auml;nga ute omv&auml;rlden och inte ansvara f&ouml;r n&aring;got eller l&ouml;sa n&aring;gra problem. Kort sagt, de beh&ouml;vde inte t&auml;nka.</span></p><p class="c0"><span class="c3"></span></p><p class="c1"><span class="c3">Det &auml;r inte de signaler du vill att din hj&auml;rna ska f&aring; efter 30 minuter av sittande. D&aring; vill du att hj&auml;rnan ska vara engagerad, uppm&auml;rksam, aktiv och angel&auml;gen om att l&ouml;sa problem. Och det &auml;r ocks&aring; precis det som h&auml;nder n&auml;r du st&auml;ller dig upp!</span></p><p class="c1"><span class="c3">T&auml;nk d&auml;rf&ouml;r p&aring; att resa dig minst en g&aring;ng i halvtimman. Det r&auml;cker att st&aring; upp en kort stund.</span></p>'),
('Våga fråga','Våga mera','<p class="c4"><span class="c1">Om man vill l&auml;ra sig nya saker s&aring; &auml;r det viktigt att fr&aring;ga! Det finns inga dumma fr&aring;gor!!! Om man inte fr&aring;gar f&aring;r man inget veta&hellip;</span></p><p class="c0"><span class="c1"></span></p><p class="c4"><span class="c1">Ibland kan det vara l&auml;tt att t&auml;nka att om jag fr&aring;gar s&aring; ser andra att jag inte f&ouml;rst&aring;r. Men det &auml;r precis tv&auml;rt om. Genom att fr&aring;ga s&aring; visar du att du vill l&auml;ra dig, att du &auml;r nyfiken. Det &auml;r ofta s&aring; att flera har liknande fr&aring;gor, du &auml;r inte ensam om att vilja veta. </span></p><p class="c0"><span class="c1"></span></p><p class="c4"><span class="c1">En annan missuppfattning &auml;r att om jag st&auml;ller fr&aring;gor s&aring; f&aring;r jag s&auml;mre bed&ouml;mning. S&aring; &auml;r det inte! Genom att fr&aring;ga kommer du att l&auml;ra dig mer och genom samtalet visar du vad du kan. </span></p><p class="c0"><span class="c1"></span></p><p class="c4"><span class="c1">Genom att fr&aring;ga s&aring; blir det ocks&aring; tydligt vad som &auml;r sv&aring;rt och l&auml;raren kan f&aring; m&ouml;jlighet att f&ouml;rklara p&aring; nya s&auml;tt. Om till exempel l&auml;rarens instruktion var otydlig. D&aring; kan dina fr&aring;gor hj&auml;lpa till att g&ouml;ra den tydligare. </span></p><p class="c0"><span class="c1"></span></p><p class="c4"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c0"><span class="c1"></span></p><p class="c4"><span class="c1">Instruktion f&ouml;r strategin</span></p><ol class="c6 lst-kix_2yf3cg4mcnf8-0 start" start="1"><li class="c3"><span class="c1">B&ouml;rja att fundera p&aring; vad du beh&ouml;ver ha svar p&aring;. Vad &auml;r det som &auml;r sv&aring;rt? Vad beh&ouml;ver klarg&ouml;ras? </span></li><li class="c3"><span class="c1">Formulera fr&aring;gor. F&ouml;rs&ouml;k att vara tydlig. Beskriv vad du beh&ouml;ver hj&auml;lp med. </span></li><li class="c3"><span class="c1">Om du har en id&eacute; men &auml;r os&auml;ker om du t&auml;nker r&auml;tt, st&auml;ll fr&aring;gan &ldquo;jag t&auml;nker s&aring; h&auml;r (om det h&auml;r problemet&hellip;) T&auml;nker jag r&auml;tt, eller har jag missf&ouml;rst&aring;tt? D&aring; visar du att du har en egen id&eacute; om l&ouml;sning. </span></li><li class="c3"><span class="c1">Om du &auml;r blyg och inte v&aring;gar fr&aring;ga, skriv fr&aring;gan i ett mejl, eller st&auml;ll fr&aring;gan ihop med en kompis. </span></li></ol>'),
('Våga göra misstag','Våga mera','<p class="c2"><span class="c0">Att v&aring;ga g&ouml;ra misstag &auml;r b&auml;sta s&auml;ttet att l&auml;ra sig nya saker.--- </span></p>'),
('Våga prata inför grupp','Våga mera','<p class="c4"><span class="c2">En del personer tycker det &auml;r sv&aring;rt och l&auml;skigt att prata inf&ouml;r andra. </span></p><p class="c4"><span class="c8">(Talmanus: Det kan till exempel bli s&aring; att man gl&ouml;mmer vad man ska s&auml;ga fast man kunde det sekunden innan, eller att man b&ouml;rjar rodna eller att man svettas om h&auml;nderna. N&aring;gra k&auml;nner sig illam&aring;ende eller b&ouml;rjar se suddigt. Upplevelsen &auml;r s&aring;klart olika f&ouml;r olika personer. Om du k&auml;nner igen dig i detta &auml;r du inte ensam.) </span></p><p class="c4"><span class="c2">Det &auml;r ganska vanligt att tycka att det &auml;r jobbigt att prata inf&ouml;r en grupp. </span></p><p class="c4 c6"><span class="c2"></span></p><p class="c4"><span class="c2">H&auml;r f&aring;r du lite tips som kan hj&auml;lpa dig. </span></p><p class="c4 c6"><span class="c2"></span></p><p class="c4"><span class="c1">Talmanus: (Det &auml;r viktigt att kunna prata inf&ouml;r grupp, &auml;ven om det &auml;r jobbigt. Det ing&aring;r som inneh&aring;ll i vissa &auml;mnen, till exempel muntlig presentation i svenska. Det &auml;r ocks&aring; en viktig f&ouml;rm&aring;ga f&ouml;r framtiden d&aring; det i m&aring;nga jobb ing&aring;r att h&aring;lla i presentationer inf&ouml;r olika grupper)</span></p><p class="c4 c6"><span class="c2"></span></p><p class="c4"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c4 c6"><span class="c2"></span></p><p class="c4"><span class="c2">Instruktion f&ouml;r strategin</span></p><p class="c4 c6"><span class="c2"></span></p><p class="c4"><span class="c2">F&ouml;rberedelse: </span></p><ol class="c3 lst-kix_hx2b7ole9r3e-0 start" start="1"><li class="c0"><span class="c2">T&auml;nk igenom vad du ska s&auml;ga. </span></li><li class="c0"><span class="c2">Skriv ner st&ouml;dord. </span></li><li class="c0"><span class="c2">&Ouml;va tyst f&ouml;r dig sj&auml;lv</span></li><li class="c0"><span class="c2">&Ouml;va h&ouml;gt framf&ouml;r en spegel</span></li><li class="c0"><span class="c2">&Ouml;va s&auml;rskilt p&aring; starten. Vilken mening b&ouml;rjar du med. l&auml;gg ocks&aring; in pauser d&auml;r du andas. </span></li><li class="c0"><span class="c2">&Ouml;va tills du k&auml;nner dig trygg med inneh&aring;llet. </span></li><li class="c0"><span class="c2">&Ouml;va en g&aring;ng till</span></li></ol><p class="c4 c6"><span class="c2"></span></p><p class="c4"><span class="c1">Talmanus: Det &auml;r viktigt att du k&auml;nner dig bekv&auml;m. V&auml;lj ut skor, och kl&auml;der du gillar och k&auml;nner dig trygg i. Be n&aring;gon av dina kompisar sitta i publiken. Be kompisen nicka och st&ouml;tta dig. Titta p&aring; kompisen, Det hj&auml;lper. F&ouml;rsta g&aring;ngen kan man beh&ouml;ver h&aring;lla sin presentation f&ouml;r endast en eller ett par kompisar man k&auml;nner sig trygg med. Men du beh&ouml;ver ocks&aring; tr&auml;na p&aring; att prata inf&ouml;r fler och fler.</span></p><p class="c4 c6"><span class="c2"></span></p><p class="c4"><span class="c2">Genomf&ouml;rande: </span></p><ol class="c3 lst-kix_ct7zcdv0tp7s-0 start" start="1"><li class="c0"><span class="c2">Att g&aring; lite fram och tillbaka precis innan du st&auml;ller dig framf&ouml;r gruppen kan hj&auml;lpa till att f&aring; kroppen att slappna av. Att hoppa lite upp och ner n&aring;gra g&aring;nger kan ocks&aring; hj&auml;lpa. </span></li><li class="c0"><span class="c2">Ta n&aring;gra djupa andetag, Det hj&auml;lper dig att syres&auml;tta hj&auml;rnan</span></li><li class="c0"><span class="c2">Att st&aring; upp hj&auml;lper dig att f&aring; energi. </span></li><li class="c0"><span class="c2">Att h&aring;lla n&aring;got i h&auml;nderna kan hj&auml;lpa. St&auml;ll en stol framf&ouml;r dig och h&aring;ll i karmen. Du kan ocks&aring; h&aring;lla i n&aring;gra talarkort d&auml;r du skrivit upp dina st&ouml;dord. </span></li><li class="c0"><span class="c2">Att komma av sig &auml;r inte farligt. Andas ett par g&aring;nger och b&ouml;rja om. En paus i presentationen &auml;r bara bra. </span></li><li class="c0"><span class="c2">Anv&auml;nd bilder som st&ouml;d. D&aring; tittar publiken p&aring; bilderna ist&auml;llet f&ouml;r p&aring; dig. Bilderna hj&auml;lper dig ocks&aring; att h&aring;lla tr&aring;den. </span></li><li class="c0"><span class="c2">Det &auml;r l&auml;tt att b&ouml;rja prata fort. Gl&ouml;m inte att andas och dra ner p&aring; tempot. </span></li><li class="c0"><span class="c2">Ha ett glas vatten n&auml;ra, Ibland k&auml;nns det sk&ouml;nt att dricka en klunk vatten om munnen blir torr. </span></li></ol><p class="c4 c6"><span class="c2"></span></p><p class="c4"><span class="c2">N&auml;r du &auml;r klar: </span></p><ol class="c3 lst-kix_xjw542spw2to-0 start" start="1"><li class="c0"><span class="c2">Var n&ouml;jd, Du gjorde det!!</span></li><li class="c0"><span class="c2">Ge nu inte upp utan forts&auml;tt att &ouml;va. Det kommer att g&aring; l&auml;ttare och l&auml;ttare. </span></li></ol>'),
('Våga redovisa','Våga mera','<p class="c5"><span class="c1">Att redovisa dina kunskaper f&ouml;r en l&auml;rare och ibland ocks&aring; andra elever, &auml;r viktigt f&ouml;r att du ska kunna g&aring; vidare i ditt skolarbete. Det &auml;r en av flera metoder som synligg&ouml;r dina kunskaper. Tycker du det &auml;r jobbigt eller sv&aring;rt att redovisa kommer h&auml;r n&aring;gra tips. </span></p><p class="c4"><span class="c1"></span></p><p class="c5"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c4"><span class="c1"></span></p><ol class="c6 lst-kix_77wrbr6iouxw-0 start" start="1"><li class="c2"><span class="c1">Boka tid f&ouml;r redovisning i god tid. </span></li><li class="c2"><span class="c1">Skriv in i loggboken vad du ska redovisa och n&auml;r. </span></li><li class="c2"><span class="c1">Passa tiden, h&aring;ll din deadline. Det du skjuter upp drabbar bara dig sj&auml;lv. </span></li><li class="c2"><span class="c1">F&ouml;r att v&aring;ga &auml;r det bra att &ouml;va s&aring; att du &auml;r trygg med vad du ska s&auml;ga/visa. </span></li></ol><ol class="c6 lst-kix_77wrbr6iouxw-1 start" start="1"><li class="c5 c7"><span>&Ouml;va inf&ouml;r en kompis. </span></li><li class="c5 c7"><span class="c3">Du kan ocks&aring; spela in sin redovisning via Screencastify och titta p&aring; den efter&aring;t f&ouml;r att fundera p&aring; vad som gick bra och vad som kan &auml;ndras och f&ouml;rb&auml;ttras</span></li></ol><ol class="c6 lst-kix_77wrbr6iouxw-0" start="5"><li class="c2"><span class="c1">Ta med en kompis du &auml;r trygg med p&aring; redovisningen. Kanske kan ni redovisa tillsammans. </span></li><li class="c2"><span class="c1">F&ouml;rbered dig p&aring; workshop, prata med din l&auml;rare, st&auml;ll fr&aring;gor om det &auml;r n&aring;got du inte f&ouml;rst&aring;r. Det finns inga dumma fr&aring;gor! </span></li><li class="c2"><span class="c1">Det g&aring;r inte att misslyckas p&aring; en redovisning. Blir n&aring;got fel s&aring; g&ouml;r det ingenting. Du f&aring;r fler m&ouml;jligheter att visa dina kunskaper. I bed&ouml;mningen s&aring; tar l&auml;raren h&auml;nsyn till de kunskaper du visar, Inte de kunskaper du inte visar. </span></li><li class="c2"><span class="c1">Se redovisningen som ett tillf&auml;lle att l&auml;ra dig. St&auml;ll fr&aring;gor om det &auml;r n&aring;got som inte f&ouml;rst&aring;r, n&aring;got du beh&ouml;ver hj&auml;lp med. </span></li><li class="c2"><span class="c1">Bel&ouml;na dig sj&auml;lv n&auml;r du redovisat. Bra gjort!</span></li></ol>'),
('Våga säga nej','Våga mera','<p class="c3"><span class="c0">Att v&aring;ga s&auml;ga nej &auml;r viktigt. Det kan ibland handla om att g&aring; emot kompisar och st&aring; emot kompistryck men det kan ocks&aring; handla om att s&auml;ga nej till en vuxen. H&auml;r &auml;r n&aring;gra tips som kan hj&auml;lpa dig att v&aring;ga s&auml;ga nej</span></p><p class="c2"><span class="c0"></span></p><p class="c3"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c2"><span class="c0"></span></p><ol class="c7 lst-kix_bex9l2bdk143-0 start" start="1"><li class="c3 c6"><span class="c0">K&auml;nn efter vad du sj&auml;lv vill. </span></li><li class="c3 c6"><span class="c0">Om det &auml;r sv&aring;rt att s&auml;ga nej p&aring; en g&aring;ng. Ge dig sj&auml;lv bet&auml;nketid genom att s&auml;ga. Jag beh&ouml;ver fundera, Kan vi prata om en stund ist&auml;llet. </span></li><li class="c3 c6"><span>&Auml;r det sv&aring;rt att veta om du vill eller inte, skriv en ja och nej lista d&auml;r du skriver olika argument. &nbsp;</span><span class="c5">Det finns ju saker man ibland m&aring;ste g&ouml;ra &auml;ven om man inte vill&hellip; (till exempel borsta t&auml;nderna) </span></li><li class="c3 c6"><span class="c0">Om det &auml;r n&aring;got du sj&auml;lv kan best&auml;mma &ouml;ver och som du inte vill. Motivera varf&ouml;r. D&aring; &auml;r det l&auml;ttare att argumentera med n&aring;gon. </span></li><li class="c3 c6"><span class="c0">&Ouml;va att s&auml;ga nej med en h&ouml;g tydlig r&ouml;st. </span></li><li class="c3 c6"><span class="c0">&Ouml;va ihop med en kompis. Om du &ouml;vat &auml;r det l&auml;ttare att s&auml;ga nej n&auml;r det verkligen &auml;r viktigt. </span></li></ol>'),
('Våga stå för din åsikt','Våga mera','<p class="c1"><span class="c2">Ibland &auml;r det sv&aring;rt att s&auml;ga vad man tycker. S&auml;rskilt om det finns flera som har en annan &aring;sikt. H&auml;r &auml;r n&aring;gra tips som du kan anv&auml;nda. </span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c1 c3"><span class="c2"></span></p><ol class="c7 lst-kix_8d98hvtjtu0-0 start" start="1"><li class="c0"><span class="c2">F&ouml;rbered dig genom att skriva ner din &aring;sikt. Det g&ouml;r att du l&auml;ttare kan s&auml;tta ord p&aring; hur du t&auml;nker. </span></li><li class="c0"><span class="c2">S&ouml;k information och fakta och hitta argument. </span></li><li class="c0"><span class="c2">Tr&auml;na p&aring; att s&auml;ga vad du tycker med kompisar som du &auml;r trygg med. </span></li><li class="c0"><span class="c2">Vad nyfiken p&aring; andras &aring;sikter. Lyssna och st&auml;ll fr&aring;gor. Det g&ouml;r det l&auml;ttare f&ouml;r andra att lyssna p&aring; dina &aring;sikter. </span></li><li class="c0"><span class="c2">T&auml;nk p&aring; att det &auml;r okej att tycka olika. Alla beh&ouml;ver inte tycka lika. </span></li></ol>'),
('10minutersregeln','Träna minnet','<p class="c2"><span class="c0">Brukar du gl&ouml;mma det du precis har l&auml;st? D&aring; &auml;r du som de flesta andra m&auml;nniskor.</span></p><p class="c2"><span class="c0">Det finns dock knep f&ouml;r att minnas det man l&auml;st och en strategi kallas f&ouml;r 10-minutersregeln.</span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2"><span class="c0">F&ouml;r de flesta fungerar minnet s&aring; att direkt efter att man l&auml;st en sak s&aring; b&ouml;rjar man gl&ouml;mma och nyckeln till att minnas det man l&auml;st &auml;r repetition. </span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><ol class="c6 lst-kix_enfy08p3c86b-0 start" start="1"><li class="c1"><span class="c0">L&auml;s texten</span></li><li class="c1"><span class="c0">10 minuter efter att du l&auml;st texten beh&ouml;ver du g&aring; igenom texten igen. Minnet fungerar n&auml;mligen s&aring;: om vi inte repeterar efter 10 minuter s&aring; gl&ouml;mmer vi ganska fort. </span></li><li class="c1"><span class="c0">Efter ett dygn beh&ouml;ver du &aring;terigen l&auml;sa samma text. Nu b&ouml;rjar kunskapen s&auml;tta sig.</span></li><li class="c1"><span class="c0">N&auml;sta g&aring;ng det &auml;r dags att repetera &auml;r efter en vecka. </span></li><li class="c1"><span class="c0">Och vill man minnas n&aring;got f&ouml;r livet s&aring; &auml;r det dags att repetera igen efter en m&aring;nad och ett halv&aring;r.</span></li></ol>'),
('Anteckna för att minnas bättre','Träna minnet','<p class="c6"><span class="c2">Brukar du ha sv&aring;rt att minnas vad din l&auml;rare pratat om p&aring; f&ouml;rel&auml;sningen eller kommunikationspasset? Eller brukar du ha sv&aring;rt att minnas det du l&auml;st?</span></p><p class="c6"><span class="c2">D&aring; &auml;r denna strategi n&aring;got f&ouml;r dig.</span></p><p class="c6"><span class="c2">F&ouml;r att minnas det man h&ouml;rt eller l&auml;st beh&ouml;ver man t&auml;nka och reflektera kring det. Ett bra s&auml;tt att g&ouml;ra det &auml;r genom att anteckna. Du h&aring;ller dig &auml;ven aktiv och utnyttjar din lektionstid genom att anteckna.</span></p><p class="c4"><span class="c2"></span></p><p class="c6"><span class="c2">Bild/filmruta</span></p><p class="c4"><span class="c2"></span></p><ol class="c1 lst-kix_wzzptb5s78t4-0 start" start="1"><li class="c6 c8"><span class="c2">Ha alltid med dig dator eller anteckningsbok och penna n&auml;r du g&aring;r p&aring; f&ouml;rel&auml;sning kommunikationspass och workshop.</span></li><li class="c6 c8"><span>Om du vill sortera och synligg&ouml;ra dina anteckningar kan du anv&auml;nda dig av olika f&auml;rger medan du skriver.</span></li><li class="c6 c8"><span class="c2">Om du tycker om att skriva anteckningar p&aring; datorn kan du anv&auml;nda dig av Google Keep som &auml;r ett till&auml;gg till Google.</span></li><li class="c6 c8"><span class="c2">Ett bra tips &auml;r att anteckna det som l&auml;raren skriver p&aring; tavlan vilket brukar vara det viktigaste ur lektionens inneh&aring;ll.</span></li></ol>'),
('Hur du minns namn','Träna minnet','<p class="c8"><span class="c3">Har du sv&aring;rt att minnas namn p&aring; personer eller platser?</span></p><p class="c8"><span class="c3">En bra strategi f&ouml;r att minnas &auml;r att g&ouml;ra om namn till bilder genom att associera.</span></p><p class="c1"><span class="c3"></span></p><p class="c8"><span class="c3">Bild/filmruta/ljudfil</span></p><p class="c1"><span class="c3"></span></p><p class="c8"><span class="c3">Instruktion f&ouml;r strategin</span></p><ol class="c10 lst-kix_cl8uu2csl5nx-0 start" start="1"><li class="c7"><span class="c6">G&ouml;r om namn till bilder. </span></li><li class="c7"><span class="c6">Bo blir kanske ett hus. Jan, en banan. </span></li><li class="c7"><span class="c6">V&auml;lj alltid det f&ouml;rsta du kommer att t&auml;nka p&aring;. Bilder med tydliga konturer minns hj&auml;rnan b&auml;st. </span></li><li class="c7"><span class="c6">Se bilden framf&ouml;r dig samtidigt som du tittar p&aring; personen, s&aring; skapas kopplingar i hj&auml;rnan och du minns.</span></li>'),
('Minnespromenad','Träna minnet','<p class="c0"><span class="c1">Om du sitter och pluggar och beh&ouml;ver l&auml;ra dig ordningen p&aring; tex historiska h&auml;ndelser d&aring; finns det en kul och lite annorlunda strategi att prova, n&auml;mligen att g&aring; en minnespromenad.</span></p><p class="c0 c2"><span class="c1"></span></p><p class="c0"><span class="c1">Bild/filmruta</span></p><p class="c0 c2"><span class="c1"></span></p><p class="c0 c2"><span class="c1"></span></p><ol class="c4 lst-kix_35z2e8fv0q6p-0 start" start="1"><li class="c0 c3"><span class="c1">Det du ska g&ouml;ra &auml;r att g&aring; en runda som du har g&aring;tt m&aring;nga g&aring;nger f&ouml;rut, d&auml;r du vet i vilken ordning som platserna ligger. </span></li><li class="c0 c3"><span class="c1">Under din promenad ska du p&aring; de h&auml;r platserna visualisera dvs skapa bilder i huvudet utifr&aring;n olika saker, h&auml;ndelser eller personer som du ska l&auml;ra dig.</span></li><li class="c0 c3"><span class="c1">Se dig omkring och anv&auml;nd din fantasi n&auml;r du promenerar.</span></li><li class="c0 c3"><span class="c1">N&auml;r du sedan sitter p&aring; provet, f&ouml;rel&auml;sningen eller seminariet kan du g&aring; promenaden igen, fast den h&auml;r g&aring;ngen i minnet och koppla ihop platserna med de h&auml;ndelser eller personer du memorerat in under din promenad</span></li></ol>'),
('Plugga glosor och begrepp med lappar','Träna minnet','<p class="c6"><span class="c2">Att plugga glosor och begrepp kan man g&ouml;ra p&aring; m&aring;nga olika s&auml;tt. H&auml;r kommer ett f&ouml;rslag p&aring; hur du kan g&ouml;ra. </span></p><p class="c0"><span class="c2"></span></p><p class="c6"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c0"><span class="c2"></span></p><p class="c6"><span class="c2">Instruktion f&ouml;r strategin</span></p><p class="c0"><span class="c2"></span></p><p class="c0"><span class="c2"></span></p><p class="c0"><span class="c2"></span></p><p class="c0"><span class="c2"></span></p><ol class="c7 lst-kix_7c6lqcak47bz-0 start" start="1"><li class="c3"><span class="c2">Ett tips n&auml;r man ska l&auml;ra sig glosor och begrepp &auml;r att skriva ner dem p&aring; lappar, med det svenska ordet p&aring; ena sidan och det spr&aring;ket du ska l&auml;ra dig p&aring; andra sidan, eller med sj&auml;lva begreppet p&aring; ena sidan och f&ouml;rklaringen p&aring; andra sidan.</span></li><li class="c3"><span class="c2">Det &auml;r l&auml;ttare att ta med sig lappar &auml;n en bok eller dator, man kan ta med sig dem &ouml;verallt och &ouml;va n&auml;r man f&aring;r n&aring;gon minut &ouml;ver.</span></li><li class="c3"><span class="c2">S&aring; fort du k&auml;nner att du kan glosorna och begreppen g&ouml;r du nya lappar.</span></li><li class="c3"><span class="c2">Kom ih&aring;g att repetera!</span></li></ol>'),
('Tidslinjer och ramsor','Träna minnet','<p class="c3"><span class="c0">En minnesteknik du kan anv&auml;nda dig av n&auml;r du tex pluggar historia &auml;r att g&ouml;ra en tidslinje och sedan &ouml;va in de olika h&auml;ndelserna med hj&auml;lpa av en ramsa.</span></p><p class="c1"><span class="c0"></span></p><p class="c3"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1"><span class="c0"></span></p><p class="c3"><span class="c0">Instruktion f&ouml;r strategin</span></p><p class="c1"><span class="c0"></span></p><ol class="c2 lst-kix_4644268be62m-0 start" start="1"><li class="c3 c7"><span class="c0">Rita upp fr&aring;n en linje fr&aring;n h&ouml;ger till v&auml;nster d&auml;r du placerar in olika namn, &aring;rtal och h&auml;ndelser och under dem det som &auml;r viktigast att veta om varje del.</span></li><li class="c3 c7"><span class="c0">Ett bra s&auml;tt att sedan komma ih&aring;g vad du skrivit p&aring; din tidslinje &auml;r att skapa ramsor. G&auml;rna knasiga ramsor som du l&auml;tt minns. </span></li><li class="c3 c7"><span class="c0">Du tar helt enkelt de ord eller begrepp som du vill minnas och skapar en ramsa efter dem. F&ouml;rsta ordet i ramsan h&auml;nder f&ouml;rst och sista ordet i ramsan h&auml;nder sist.</span></li></ol>'),
('Repetitionskort','Träna minnet','<p class="c1"><span class="c3">N&auml;r du l&auml;ser en text och beh&ouml;ver repetera inneh&aring;llet &auml;r repetitionskort en bra strategi. Repetitionskort best&aring;r ofta av lagom mycket fakta i form av punktlistor, nyckelord eller bilder. Ditt minne kommer att f&ouml;rb&auml;ttras om du anv&auml;nder dessa kort regelbundet.</span></p><p class="c1 c2"><span class="c3"></span></p><p class="c1"><span class="c3">Bild/filmruta/ljudfil</span></p><p class="c1 c2"><span class="c3"></span></p><p class="c1"><span class="c3">Instruktion f&ouml;r strategin</span></p><ol class="c5 lst-kix_rtxgryf1g27r-0 start" start="1"><li class="c0"><span>Fundera &ouml;ver vad ska du skriva p&aring; </span><span>repetitionskorten</span><span class="c3">.</span></li><li class="c0"><span class="c3">Om du vill g&ouml;ra dina repetitionskort p&aring; datorn kan du anv&auml;nda Google Teckning, men du kan &auml;ven g&ouml;ra dina kort f&ouml;r hand med ett vanligt papper och en penna.</span></li><li class="c0"><span class="c3">Sammanfatta information i punktlistor, nyckelord eller bilder.</span></li><li class="c0"><span class="c3">Undvik att anv&auml;nda dig av fullst&auml;ndiga meningar.</span></li><li class="c0"><span class="c3">En del elever tycker det &auml;r bra att anv&auml;nda olika f&auml;rger och korta p&aring;minnelser, eller tips h&ouml;gst upp p&aring; kortet f&ouml;r att v&auml;cka minnet.</span></li><li class="c0"><span class="c3">Kom ih&aring;g att m&aring;let &auml;r att f&ouml;rst&aring; inneh&aring;llet, inte memorera godtycklig fakta.</span></li></ol>'),
('Tankekartor','Träna minnet','<p class="c8"><span>Tankekartor &auml;r ett utm&auml;rkt arbetsredskap f&ouml;r att sortera sina tankar inf&ouml;r tex en skrivuppgift, f&ouml;r att komma ih&aring;g en text eller kunna &aring;terber&auml;tta en text med egna ord.T</span><span>ankekartor kan anv&auml;ndas och g&ouml;ras p&aring; m&aring;nga olika s&auml;tt det viktigaste &auml;r inte att skriva hela f&ouml;rklarande meningar utan bara </span><span class="c4">enstaka ord.</span></p><p class="c3"><span class="c4"></span></p><p class="c1"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c1 c9"><span class="c2"></span></p><p class="c1"><span>Instruktion f&ouml;r strategin</span></p><p class="c1 c9"><span class="c2"></span></p><p class="c3"><span class="c2"></span></p><ol class="c10 lst-kix_qy3g9qqx3pdw-0 start" start="1"><li class="c0"><span class="c2">G&ouml;r huvudrubriker som passar &auml;mnet och ringa in dessa s&aring; att de blir tydliga.</span></li><li class="c0"><span class="c2">Fyll p&aring; varje huvudrubrik med de ord eller begrepp som du ska minnas,</span></li><li class="c0"><span class="c2">Det &auml;r bra att anv&auml;nda fr&aring;geord som rubriker, t.ex.: Vad?, N&auml;r?, Var?, Hur?, Varf&ouml;r?, Vem/Vilka?</span></li><li class="c0"><span class="c2">F&ouml;r att se samband kan du anv&auml;nda dig av olika f&auml;rger och d&aring; anv&auml;nda samma f&auml;rg till omr&aring;den som h&ouml;r ihop.</span></li><li class="c0"><span class="c2">Vill du ist&auml;llet g&ouml;ra digitala tankekartor kan du anv&auml;nda dig av verktygen Mindmeister eller Mindmup. Dessa kan du &auml;ven anv&auml;nda tillsammans med kompisar f&ouml;r att g&ouml;ra gemensamma tankekartor.</span></li></ol>'),
('Träna glosor och begrepp med hjälp av association','Träna minnet','<p class="c2"><span class="c3">Att associera n&auml;r du &ouml;var glosor och begrepp kan vara v&auml;ldigt effektivt f&ouml;r ditt l&auml;rande.</span></p><p class="c0"><span class="c3"></span></p><p class="c0"><span class="c3"></span></p><p class="c2"><span class="c4">Bild/filmruta/ljudfil</span></p><p class="c0"><span class="c4"></span></p><p class="c2"><span class="c4">Instruktion f&ouml;r strategin</span></p><p class="c2"><span class="c3">N&auml;r du ska l&auml;ra dig nya ord och begrepp hj&auml;lper det om du associerar till andra ord eller saker som du redan k&auml;nner till. </span></p><p class="c2"><span class="c3">Det en kan relatera till l&auml;r en sig n&auml;mligen enklare, eftersom det blir mer verkligt. Associera fritt, men t&auml;nk p&aring; att dina associationer inte f&aring;r vara f&ouml;r l&aring;ngs&ouml;kta, d&aring; kan du r&aring;ka tappa bort vad du skulle l&auml;ra dig!</span></p>'),
('Komma på en ny idé genom “brainstorming”','Vara innovativ','<p class="c1"><span class="c0">I vissa uppgifter beh&ouml;ver du komma p&aring; en id&eacute;. Det kan till exempel vara att du ska komma p&aring; en story till en uppsats i svenska, eller du beh&ouml;ver komma p&aring; en id&eacute; till en bilduppgift. Det &auml;r l&auml;tt h&auml;nt att man l&aring;ser sig f&ouml;r att man vill komma p&aring; en BRA id&eacute;. Genom en &ldquo;brainstorming&rdquo; &ouml;ppnar du upp dina tankeprocesser som g&ouml;r att det &auml;r l&auml;ttare att komma p&aring; id&eacute;er. </span></p><p class="c1 c3"><span class="c0"></span></p><p class="c1"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1 c3"><span class="c0"></span></p><ol class="c4 lst-kix_2n2ydjoeewb9-0 start" start="1"><li class="c1 c2"><span class="c0">Ta ett tomt papper</span></li><li class="c1 c2"><span class="c0">Skriv uppgifters tema i mitten</span></li><li class="c1 c2"><span>Skriv ner </span><span class="c8">ALLA</span><span class="c0">&nbsp;id&eacute;er du kommer p&aring;. V&auml;rdera inte (Du beh&ouml;ver inte tycka en ide &auml;r bra f&ouml;r att skriva ner den) G&ouml;r detta i 5 minuter. </span></li><li class="c1 c2"><span class="c0">Ta en kort paus</span></li><li class="c1 c2"><span class="c0">Titta nu p&aring; alla id&eacute;er. Rangordna genom att ringa in alla id&eacute;er som &auml;r m&ouml;jliga att genomf&ouml;ra. minst 3 och max 5</span></li><li class="c1 c2"><span class="c0">Stryk om det &auml;r n&aring;gra id&eacute;er som &auml;r helt om&ouml;jliga att genomf&ouml;ra. Du f&aring;r inte stryka mer &auml;n h&auml;lften. </span></li><li class="c1 c2"><span class="c0">Veckla ut de id&eacute;er du nu har. Hur skulle de fungera? vad beh&ouml;ver du ta reda p&aring;? Hur kan de bli verklighet? &Auml;r det n&aring;got material som beh&ouml;vs? </span></li><li class="c1 c2"><span class="c0">Nu kan du v&auml;lja ut de tre id&eacute;er som du tycker b&auml;st om. S&auml;tt en stj&auml;rna vid de som du v&auml;ljer ut. </span></li><li class="c1 c2"><span class="c0">Ber&auml;tta f&ouml;r en kompis om dina tre id&eacute;er. </span></li><li class="c1 c2"><span class="c0">V&auml;lj nu en av de tre id&eacute;er du kommit p&aring;. </span></li></ol>'),
('Komma på en story genom att använda slumpen','Vara innovativ','<p class="c1"><span class="c0">N&auml;r man ska skriva eller ber&auml;tta en story kan det h&auml;nda att det &auml;r l&auml;tt att fastna. Att det tar &nbsp;l&aring;ng tid att komma p&aring; sj&auml;lva storyn. Ta hj&auml;lp av slumpen ist&auml;llet s&aring; har du mer tid &aring;t sj&auml;lva ber&auml;ttandet, skrivandet. </span></p><p class="c1 c3"><span class="c0"></span></p><p class="c1"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1 c3"><span class="c0"></span></p><ol class="c5 lst-kix_kgdl1g9ba02p-0 start" start="1"><li class="c1 c2"><span class="c0">N&auml;r du ska hitta p&aring; en story beh&ouml;ver 1. en karakt&auml;r (huvudperson), 2. plats och tidpunkt, och 3. n&aring;got som ska h&auml;nda</span></li><li class="c1 c2"><span class="c0">Ta ett tomt papper som du klipper/river s&aring; att du f&aring;r 30 papperslappar.</span></li><li class="c1 c2"><span class="c0">P&aring; 10 papperslappar skriver du olika karakt&auml;rer (huvudpersoner); Till exempel: 14 &aring;rig fotbollsspelare, gammal tant som g&aring;r med k&auml;pp&hellip;. </span></li><li class="c1 c2"><span class="c0">P&aring; 10 papperslappar skriver du olika platser och tidpunkter: Till exempel; p&aring; stranden - eftermiddag, I skolan p&aring; vintern&hellip;</span></li><li class="c1 c2"><span class="c0">P&aring; 10 papperslappar skriver du olika h&auml;ndelser. Till exempel: En kompis som ska komma &auml;r f&ouml;rsenad... N&aring;got har g&aring;tt s&ouml;nder och m&aring;ste lagas&hellip; </span></li><li class="c1 c2"><span class="c0">Vik ihop lapparna och l&auml;gg i tre h&ouml;gar. Anv&auml;nd slumpen och dra en lapp fr&aring;n varje h&ouml;g. Du har nu f&aring;tt en &ldquo;storyline&rdquo; att anv&auml;nda till din story. </span></li></ol><p class="c1 c3 c9"><span class="c0"></span></p><p class="c1 c4"><span class="c0">Till exempel: Det var en g&aring;ng en gammal tant som g&aring;r med k&auml;pp f&ouml;r att hon hade ont i ena foten. Hon promenerar sakta p&aring; stranden en solig en eftermiddag och v&auml;ntar p&aring; en gammal v&auml;n. Telefonen ringer och v&auml;nnen &auml;r f&ouml;rsenad.. VAD h&auml;nder nu? </span></p><p class="c1 c3 c4"><span class="c0"></span></p><ol class="c5 lst-kix_kgdl1g9ba02p-0" start="7"><li class="c1 c2"><span class="c0">Denna strategi funkar n&auml;r du ska skriva ber&auml;ttelser, skapa en serie, g&ouml;ra en film osv. </span></li><li class="c1 c2"><span class="c0">Samarbeta g&auml;rna med denna strategi. D&aring; f&aring;r ni fler lappar i varje kategori och det kan bli &auml;nd&aring; roligare att skriva om en karakt&auml;r som n&aring;gon annan hittat p&aring;. </span></li></ol>'),
('Samla inspiration för nya idéer','Vara innovativ','<p class="c2"><span class="c3">N&auml;r man ska komma p&aring; n&aring;got nytt &auml;r det l&auml;tt att k&ouml;ra fast. Ett s&auml;tt att komma p&aring; nya id&eacute;er &auml;r att leta inspiration och samla id&eacute;er som andra har gjort. Du samlar bilderna i form av ett bildcollage</span></p><p class="c0"><span class="c3"></span></p><p class="c2"><span class="c3">Bild/filmruta/ljudfil</span></p><p class="c0"><span class="c3"></span></p><ol class="c7 lst-kix_4xbisgna2wmw-0 start" start="1"><li class="c1 c6"><span class="c3">Leta inspiration inom det omr&aring;de du t&auml;nkt p&aring; n&auml;tet, i tidningar, i b&ouml;cker, ute.... </span></li><li class="c1 c6"><span class="c3">Du samlar bilderna, klipper ut det du gillar och s&auml;tter ihop dem till ett bildcollage</span></li><li class="c1 c6"><span class="c3">Collaget hj&auml;lper dig att utveckla din id&eacute;</span></li></ol><p class="c0"><span class="c3"></span></p><p class="c1"><span class="c3">N&aring;gra exempel: </span></p><p class="c2 c5"><span class="c3">Du har en uppgift i sl&ouml;jd d&auml;r du ska g&ouml;ra ett k&ouml;ksf&ouml;rem&aring;l. Du gillar sport. Du letar bilder p&aring; k&ouml;ksf&ouml;rem&aring;l, titta p&aring; bilder med sport. N&auml;r du tittar p&aring; collaget s&aring; ser du fotbollens m&ouml;nster. Det m&ouml;nstret inspirerar dig till att g&ouml;ra en k&ouml;kshandduk med fotbollsm&ouml;nster. </span></p><p class="c0 c5"><span class="c3"></span></p><p class="c2 c5"><span class="c3">Du ska skriva en ber&auml;ttelse som utspelar sig i Marocko. Du hittar bilder fr&aring;n Marocko som g&ouml;r att du l&auml;ttare kan komma p&aring; olika h&auml;ndelser och milj&ouml;er d&auml;rifr&aring;n. </span></p>'),
('Ta snabba anteckningar på dator','Skriva','<p class="c2"><span class="c3">N&auml;r du g&aring;r p&aring; en f&ouml;rel&auml;sning &auml;r det en bra id&eacute; att ta anteckningar. Men f&ouml;r att fokus inte ska ligga p&aring; anteckningsboken eller p&aring; datorn utan ist&auml;llet det som l&auml;rare f&ouml;rmedlar &auml;r det smart att ta snabba anteckningar.</span></p><p class="c2 c4"><span class="c3"></span></p><p class="c2 c4"><span class="c3"></span></p><p class="c2"><span class="c3">Bild/filmruta/ljudfil</span></p><p class="c2 c4"><span class="c3"></span></p><p class="c2 c4"><span class="c3"></span></p><ol class="c1 lst-kix_dpngk6h6ort9-0 start" start="1"><li class="c0"><span>Plocka fram din </span><span>dator.</span><span class="c3">&nbsp;Anteckna vad f&ouml;rel&auml;sningen handlar om och dagens datum.</span></li><li class="c0"><span class="c3">Anv&auml;nd g&auml;rna olika f&auml;rger s&aring; du kan strukturera dina anteckningar utifr&aring;n omr&aring;de. Ett omr&aring;de = en f&auml;rg.</span></li><li class="c0"><span class="c3">Om du vill skriva dina anteckningar tillsammans med en kompis kan ni dela Google Dokument eller Google Presentation tillsammans.</span></li><li class="c0"><span class="c3">Det g&aring;r snabbare att anteckna om du anv&auml;nder f&ouml;rkortningar. </span></li><li class="c0"><span class="c3">H&auml;r nedan finner du tips p&aring; f&ouml;rkortningar. Du kan s&auml;kert komma p&aring; &auml;nnu fler sj&auml;lv.</span></li></ol>'),
('Skrivmallar','Skriva','<p class="c2"><span class="c0">Skrivmallar &auml;r till f&ouml;r att du ska komma ig&aring;ng med ditt skrivande snabbare och f&aring; till en bra och tydlig struktur.</span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2"><span class="c6">Under KS Akademi f&ouml;r elever</span><span class="c0">&nbsp;hittar du en m&auml;ngd olika skrivmallar. L&auml;s din uppgift som du ska g&ouml;ra och ta reda p&aring; vilken typ av text du ska skriva. Kolla igenom listan av skrivmallar, det kan finnas fler som passar till uppgiften. Be din l&auml;rare om hj&auml;lp att v&auml;lja en skrivmall om du &auml;r os&auml;ker.</span></p>'),
('Ta snabba anteckningar','Skriva','<p class="c0"><span class="c1">N&auml;r du g&aring;r p&aring; en f&ouml;rel&auml;sning &auml;r det en bra id&eacute; att ta anteckningar. Men f&ouml;r att fokus inte ska ligga p&aring; anteckningsboken utan ist&auml;llet det som l&auml;rare f&ouml;rmedlar &auml;r det smart att ta snabba anteckningar.</span></p><p class="c0 c4"><span class="c1"></span></p><p class="c0 c4"><span class="c1"></span></p><p class="c0"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c0 c4"><span class="c1"></span></p><p class="c0 c4"><span class="c1"></span></p><ol class="c6 lst-kix_dpngk6h6ort9-0 start" start="1"><li class="c0 c5"><span class="c1">Plocka fram ditt anteckningsblock. &nbsp;Anteckna vad f&ouml;rel&auml;sningen handlar om och dagens datum.</span></li><li class="c0 c5"><span class="c1">Tips! Helvita papper ger dig st&ouml;rre frihet att rita figurer och tankekartor. Du kan anv&auml;nda olika storlekar p&aring; bokst&auml;verna n&auml;r du s&auml;tter rubriker eller vill betona viktiga saker och ibland vill du kanske skriva tv&auml;rs &ouml;ver pappret.</span></li><li class="c0 c5"><span class="c1">Anv&auml;nd g&auml;rna olika f&auml;rger p&aring; pennan s&aring; du kan strukturera dina anteckningar utifr&aring;n omr&aring;de. Ett omr&aring;de = en f&auml;rg.</span></li><li class="c0 c5"><span class="c1">Det g&aring;r snabbare att anteckna om du anv&auml;nder f&ouml;rkortningar. </span></li><li class="c0 c5"><span class="c1">H&auml;r nedan finner du tips p&aring; f&ouml;rkortningar. Du kan s&auml;kert komma p&aring; &auml;nnu fler sj&auml;lv.</span></li></ol>'),
('Skriva uppsats','Skriva','<p class="c4"><span class="c1">Inf&ouml;r en uppsats kan du inte f&ouml;rbereda dig p&aring; samma s&auml;tt som inf&ouml;r ett prov. Men det &auml;r &auml;nd&aring; viktigt att du f&ouml;rbereder dig s&aring; gott du kan.</span></p><p class="c0"><span class="c1"></span></p><p class="c0"><span class="c1"></span></p><p class="c0"><span class="c1"></span></p><p class="c0"><span class="c1"></span></p><p class="c4"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c0"><span class="c1"></span></p><p class="c4"><span class="c1">Instruktion f&ouml;r strategin</span></p><ol class="c5 lst-kix_22shnynwekry-0 start" start="1"><li class="c2"><span class="c1">Ha koll p&aring; de vanligaste textgenrerna - hur ska texten skrivas?</span></li><li class="c2"><span class="c3">Fundera till exempel p&aring; vem mottagaren &auml;r och anpassa spr&aring;ket d&auml;refter. </span></li><li class="c2"><span class="c3">&Auml;gna n&aring;gra minuter till att reflektera kring &auml;mnet, vad tycker du &auml;r intressant och vad kan du spinna vidare p&aring;?</span></li><li class="c2"><span class="c3">Skriv ner ord och formuleringar som dyker upp i huvudet p&aring; ett papper.</span></li><li class="c2"><span class="c3">V&auml;nta med inledning till sist om det k&auml;nns tr&ouml;gt att komma ig&aring;ng</span></li><li class="c2"><span class="c3">Variera ditt spr&aring;k. Anv&auml;nd sambandsord, synonymer</span></li><li class="c2"><span class="c3">Dela in texten i stycken</span></li><li class="c2"><span class="c3">H&aring;ll dig till den r&ouml;da tr&aring;den och variera spr&aring;ket.</span></li><li class="c2"><span class="c3">Runda av och sammanfatta det du tycker &auml;r viktigast i din text</span></li><li class="c2"><span class="c3">L&auml;s igenom</span></li><li class="c2"><span class="c3">Be om respons och bearbeta utifr&aring;n den.</span></li></ol>'),
('Kolla din text','Skriva','<p class="c5"><span class="c1">N&auml;r du har arbetat med en text en l&auml;ngre period &auml;r det l&auml;tt att bli hemmablind och inte se eventuella fel eller saker du beh&ouml;ver f&ouml;rtydliga, f&ouml;rb&auml;ttra eller korrigera. D&auml;rf&ouml;r &auml;r det bra att kolla av din text innan du l&auml;mnar in den f&ouml;r respons och bed&ouml;mning.</span></p><p class="c2"><span class="c1"></span></p><p class="c2"><span class="c1"></span></p><p class="c5"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c2"><span class="c1"></span></p><p class="c5"><span class="c1">Instruktion f&ouml;r strategin</span></p><ol class="c7 lst-kix_d4t0k2mfw3fo-0 start" start="1"><li class="c3"><span class="c1">L&auml;s igenom din text b&aring;de tyst och h&ouml;gt f&ouml;r dig sj&auml;lv och fundera &ouml;ver f&ouml;ljande:</span></li><li class="c3"><span class="c1">Har arbetet en titel och &auml;r ditt namn med?</span></li><li class="c3"><span class="c1">&Auml;r alla riktlinjer f&ouml;r texten uppfyllda?</span></li><li class="c3"><span class="c1">Har arbetet blivit korrekturl&auml;st f&ouml;r att hitta eventuella stavfel?</span></li><li class="c3"><span class="c1">&Auml;r grammatiken och interpunktionen korrekt i arbetet?</span></li><li class="c3"><span class="c1">&Auml;r alla meningar fullst&auml;ndiga?</span></li><li class="c3"><span class="c1">&Auml;r strukturen logisk? </span></li><li class="c3"><span class="c1">&Auml;r inneh&aring;llet tydligt och l&auml;tt att f&ouml;rst&aring;? </span></li><li class="c3"><span class="c1">Har alla punkter f&ouml;rklarats, med exempel, om n&ouml;dv&auml;ndigt?</span></li><li class="c3"><span class="c1">Har alla id&eacute;er fr&aring;n andra k&auml;llor f&aring;tt en h&auml;nvisning?</span></li></ol>'),
('Att skriva en vetenskaplig text','Skriva','<p class="c3"><span class="c5">I de naturvetenskapliga &auml;mnena ing&aring;r det att skriva vetenskapliga texter. Ofta handlar det om inl&auml;mningsuppgifter som ska bed&ouml;mas. En vetenskaplig text skiljer sig fr&aring;n en sk&ouml;nlitter&auml;r text framf&ouml;rallt eftersom den bygger p&aring; fakta och analys. </span></p><p class="c3 c4"><span class="c5"></span></p><p class="c3 c4"><span class="c5"></span></p><p class="c3"><span class="c5">Bild/filmruta/ljudfil</span></p><p class="c3 c4"><span class="c5"></span></p><p class="c3 c4"><span class="c5"></span></p><p class="c3"><span class="c5">Att t&auml;nka p&aring; n&auml;r du skriver en vetenskaplig text:</span></p><ul class="c6 lst-kix_sxjy3be3zjac-0 start"><li class="c1"><span class="c5">L&auml;s uppgiften och var noga med att du f&ouml;rst&aring;r vad den g&aring;r ut p&aring;</span></li><li class="c1"><span class="c5">Finns det bed&ouml;mningsmatriser s&aring; l&auml;s och anv&auml;nd dessa under tiden du skriver.</span></li><li class="c1"><span class="c5">Vilka begrepp b&ouml;r du ha med? Det &auml;r viktigt att spr&aring;ket &auml;r naturvetenskapligt.</span></li><li class="c1"><span class="c5">Vilka resurser (k&auml;llor) har du? Texter, filmer, genomg&aring;ngar, webbsidor</span></li><li class="c1"><span class="c5">G&ouml;r en tankekarta om du vill.</span></li><li class="c1"><span class="c5">Anv&auml;nd g&auml;rna en skrivmall. S&ouml;k p&aring; n&auml;tet eller fr&aring;ga din l&auml;rare. </span></li><li class="c1"><span class="c5">Planera ditt skrivande. Att skriva en vetenskaplig text &auml;r en process s&aring; v&auml;nta inte med att b&ouml;rja skriva. </span></li><li class="c1"><span class="c5">Variera ditt spr&aring;k, anv&auml;nd synonymer. G&ouml;r styckeindelningar och g&auml;rna underrubriker. </span></li><li class="c1"><span class="c5">N&auml;r du &auml;r klar s&aring; l&auml;s igenom och bearbeta din text, j&auml;mf&ouml;r med bed&ouml;mningsmatrisen</span></li><li class="c1"><span class="c5">Ta respons av l&auml;rare eller en kompis.</span></li><li class="c1"><span class="c5">Bearbeta texten en sista g&aring;ng efter responsen som du f&aring;tt. Fundera &auml;ven &ouml;ver responsen som du gett, kanske fick du ett tips som du kan anv&auml;nda i din egen text.</span></li><li class="c1"><span class="c5">L&auml;mna in!</span></li></ul>'),
('Anteckna med smart mall','Skriva','<p class="c2"><span class="c0">En bra anteckningsteknik &auml;r viktig, b&aring;de n&auml;r du l&auml;ser och n&auml;r du lyssnar p&aring; en f&ouml;rel&auml;sning. N&auml;r du antecknar reducerar du ner texten eller f&ouml;rel&auml;sningen vilket senare g&ouml;r det enklare att repetera.</span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2"><span class="c0">Instruktion f&ouml;r strategin</span></p><p class="c2 c3"><span class="c0"></span></p><ol class="c4 lst-kix_w2senmfurwn-0 start" start="1"><li class="c1"><span class="c0">Dra en linje ca 5-6 cm in fr&aring;n v&auml;nsterkanten p&aring; sidan uppifr&aring;n och n&auml;stan &auml;nda ner p&aring; en anteckningssida, l&auml;mna 5-4 cm. </span></li><li class="c1"><span class="c0">Dra sedan en v&aring;gr&auml;t linje i nedre delen av anteckningsrutan.</span></li><li class="c1"><span class="c0">Nu har du tre f&auml;lt. Ett ganska smalt till v&auml;nster, ett st&ouml;rre till h&ouml;ger och ett f&auml;lt l&auml;ngst ner p&aring; sidan.</span></li><li class="c1"><span class="c0">I det v&auml;nstra f&auml;ltet skriver du in nyckelord och huvudteman.</span></li><li class="c1"><span class="c0">I den h&ouml;gra f&auml;ltet skriver du in dina anteckningar. Skriv huvudpunkter, citat, diagram osv.</span></li><li class="c1"><span class="c0">I f&auml;ltet l&auml;ngst ner skriver du din sammanfattning av texten eller f&ouml;rel&auml;sningen p&aring; 1-2 meningar.</span></li><li class="c1"><span class="c0">Nu har du antecknat nyckelord,huvudpunkter, citat och en sammanfattning. </span></li><li class="c1"><span class="c0">N&auml;r du sedan ska repetera texten eller f&ouml;rel&auml;sningen blir det enkelt att f&ouml;rh&ouml;ra dig sj&auml;lv genom att t&auml;cka f&ouml;r det h&ouml;gra f&auml;ltet och &aring;terber&auml;tta genom att prata utifr&aring;n nyckelorden i det v&auml;nstra f&auml;ltet.</span></li></ol>'),
('Att bredda en diskussion','Samarbeta','<p class="c3"><span class="c9">Att kunna samtala och resonera ing&aring;r i flera &auml;mnen i skolan. F&ouml;r att bidra till en diskussion och sj&auml;lv f&aring; ut n&aring;gonting av den finns det n&aring;gra saker som &auml;r bra att t&auml;nka p&aring;. </span></p><p class="c3 c6"><span class="c2"></span></p><p class="c3"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c0"><span class="c2"></span></p><ol class="c7 lst-kix_6qla26rqjg1x-0 start" start="1"><li class="c3 c4"><span class="c2">Att prata &auml;r bra men du beh&ouml;ver &auml;ven lyssna p&aring; vad de andra s&auml;ger. D&aring; f&aring;r du en st&ouml;rre f&ouml;rst&aring;else och resonemanget breddas.</span></li><li class="c3 c4"><span class="c2">Bjud in alla i samtalet, s&aring; att alla f&aring;r prata.</span></li><li class="c3 c4"><span class="c2">St&auml;ll f&ouml;ljdfr&aring;gor som hj&auml;lper dig att f&ouml;rst&aring; den andras resonemang, ditt eget resonemang kommer f&ouml;rdjupas av detta.</span></li><li class="c3 c4"><span class="c2">Om du ska kunna bredda ett resonemang &auml;r det viktigt att lyssna p&aring; vad de andra s&auml;ger, men &auml;ven kunna spinna vidare p&aring; det de s&auml;ger.</span></li><li class="c3 c4"><span class="c2">T&auml;nk p&aring; samtalstonen. Att respektera alla som &auml;r med i grupparbetet &auml;r en sj&auml;lvklarhet och grupparbetet blir b&auml;ttre.</span></li></ol>'),
('Få ut det mesta av grupparbetet','Samarbeta','<p class="c2"><span class="c1">Kommunikation med de andra i gruppen &auml;r avg&ouml;rande f&ouml;r alla grupparbeten och projekt. En annan viktig del i att lyckas med sitt grupparbete &auml;r att f&ouml;rdela roller och ansvarsomr&aring;den.</span></p><p class="c2 c3"><span class="c1"></span></p><p class="c2 c3"><span class="c1"></span></p><p class="c2 c3"><span class="c1"></span></p><p class="c2 c3"><span class="c1"></span></p><p class="c2"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c2 c3"><span class="c1"></span></p><p class="c2"><span class="c1">Instruktion f&ouml;r strategin</span></p><p class="c2 c3"><span class="c1"></span></p><p class="c2 c3"><span class="c1"></span></p><ol class="c0 lst-kix_gd1vpqou0eq-0 start" start="1"><li class="c2 c5"><span class="c1">F&ouml;rsta g&aring;ngen gruppen tr&auml;ffas &auml;r det viktigt att alla i gruppen &auml;r medvetna om tre viktiga fr&aring;gor.</span></li></ol><ol class="c0 lst-kix_gd1vpqou0eq-1 start" start="1"><li class="c2 c8"><span class="c1">Vad g&aring;r uppgiften ut p&aring;?</span></li><li class="c2 c8"><span>N&auml;r ska den vara klar?</span></li></ol><ol class="c0 lst-kix_gd1vpqou0eq-0" start="2"><li class="c2 c5"><span class="c1">N&auml;r ni v&auml;l har en samsyn p&aring; vad uppgiften g&aring;r ut p&aring; och n&auml;r den ska vara klar beh&ouml;ver ni f&ouml;rdela roller i gruppen.</span></li><li class="c2 c5"><span class="c1">Inom grupparbeten &auml;r det viktigt att inta olika roller, ibland f&aring;r man v&auml;lja roll och ibland blir man tilldelad en roll.</span></li><li class="c2 c5"><span class="c1">Fundera tillsammans vilka roller just ni beh&ouml;ver i grupparbetet och vem som g&ouml;r vad.</span></li><li class="c2 c5"><span class="c1">Nu n&auml;r ni satt strukturen f&ouml;r grupparbetet kan ni k&ouml;ra ig&aring;ng med sj&auml;lva inneh&aring;llet.</span></li></ol>'),
('Repetitionskompis','Samarbeta','<p class="c3"><span class="c0">Det fungerar inte f&ouml;r alla elever att repetera p&aring; egen hand. Att repetera med en v&auml;n, klasskompis eller sl&auml;kting kan hj&auml;lpa dig att fokusera p&aring; ett &auml;mne.</span></p><p class="c1"><span class="c0"></span></p><p class="c1"><span class="c0"></span></p><p class="c1"><span class="c0"></span></p><p class="c1"><span class="c0"></span></p><p class="c3"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1"><span class="c0"></span></p><p class="c3"><span class="c0">Instruktion f&ouml;r strategin</span></p><p class="c1"><span class="c0"></span></p><ol class="c2 lst-kix_tm5jsd83t8r3-0 start" start="1"><li class="c3 c4"><span class="c0">B&ouml;rja med att fr&aring;ga en v&auml;n eller sl&auml;kting om de vill vara din repetitionskompis.</span></li><li class="c3 c4"><span class="c0">G&aring; igenom igenom studiematerialet tillsammans och hj&auml;lp varandra att f&ouml;rklara saker som ni &auml;r os&auml;kra p&aring;.</span></li><li class="c3 c4"><span class="c0">N&auml;r ni b&aring;da k&auml;nner att ni &auml;r ganska s&auml;kra p&aring; studiematerialet kan ni f&ouml;rh&ouml;ra varandra f&ouml;r att se hur mycket ni kommer ih&aring;g.</span></li><li class="c3 c4"><span class="c0">Det ni inte minns beh&ouml;ver ni g&aring; igenom igen genom att f&ouml;rklara f&ouml;r varandra igen tills kunskaperna sitter.</span></li><li class="c3 c4"><span class="c0">Dela g&auml;rna med er av era b&auml;sta minnesknep.</span></li></ol>'),
('Skaffa en studybuddy','Samarbeta','<p class="c5"><span class="c0">Att arbeta tillsammans med en kompis brukar vara en givande strategi. F&ouml;r att g&ouml;ra samarbetet s&aring; bra som m&ouml;jligt &auml;r det viktigt att gemensamt organisera s&aring; ni b&aring;da f&aring;r ut s&aring; mycket som m&ouml;jligt av det.</span></p><p class="c3"><span class="c0"></span></p><p class="c5"><span class="c0">G&ouml;r s&aring; h&auml;r:</span></p><p class="c3"><span class="c0"></span></p><p class="c3"><span class="c0"></span></p><ol class="c6 lst-kix_beicy7iq8n0z-0 start" start="1"><li class="c2"><span>Skaffa en </span><span class="c1">studybuddy</span><span class="c0">, &nbsp;en studiekompis.</span></li><li class="c2"><span>G&ouml;r en </span><span class="c1">&ouml;verenskommelse</span><span>&nbsp;om att st&ouml;dja varandra i studierna p&aring; ett systematiskt s&auml;tt och </span><span class="c0 c4">s&auml;tt upp gemensamma regler f&ouml;r hur ni ska st&ouml;tta varandra.</span></li><li class="c2"><span>Best&auml;m under </span><span class="c1">hur l&aring;ng tid </span><span class="c0">ni ska st&ouml;tta varandra.</span></li><li class="c2"><span>Best&auml;m </span><span class="c1">hur ofta</span><span class="c0">&nbsp;ni ska tr&auml;ffas och st&ouml;tta varandra. </span></li><li class="c2"><span class="c0">Prata igenom hur ni ska ge varandra positiv och utvecklande feedback</span></li><li class="c2"><span class="c0">S&auml;tt er efter en f&ouml;rel&auml;sning och red ut eventuella fr&aring;getecken tillsammans.</span></li><li class="c2"><span>Dela med er av och j&auml;mf&ouml;r era anteckningar</span><span>&nbsp;</span></li><li class="c2"><span class="c0">Plugga tillsammans inf&ouml;r prov och inl&auml;mningar genom att f&ouml;rh&ouml;ra varandra och ge varandra feedback p&aring; inneh&aring;ll och spr&aring;k.</span></li><li class="c2"><span class="c0">Utv&auml;rdera samarbetet kontinuerligt med fokus p&aring; hur samarbetet kan bli &auml;nnu b&auml;ttre.</span></li><li class="c2"><span class="c0">Gl&ouml;m inte att fira med din studybuddy n&auml;r ni har lyckats!</span></li></ol>'),
('Ta hjälp av varandra','Samarbeta','<p class="c3"><span class="c0">Att plugga tillsammans med andra &auml;r oslagbart f&ouml;r inl&auml;rningen. Ni kan diskutera det ni inte f&ouml;rst&aring;r, och ibland &auml;r det l&auml;ttare att st&auml;lla fr&aring;gor till en kompis &auml;n till en l&auml;rare. Att plugga tillsammans med andra g&ouml;r det ocks&aring; till en social grej.</span></p><p class="c2"><span class="c0"></span></p><p class="c3"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c2"><span class="c0"></span></p><p class="c3"><span class="c0">Instruktion f&ouml;r strategin</span></p><p class="c2"><span class="c0"></span></p><ol class="c6 lst-kix_xw4k4o29kq5m-0 start" start="1"><li class="c3 c5"><span class="c0">Ett bra s&auml;tt att samarbeta p&aring; &auml;r att turas om att f&ouml;rklara saker f&ouml;r varandra. D&aring; f&aring;r ni reda p&aring; vad ni kan och vad ni beh&ouml;ver tr&auml;na mer p&aring;. </span></li><li class="c3 c5"><span class="c0">Men t&auml;nk p&aring; att det finns en risk att det blir mer snack om annat, s&aring; h&aring;ll fokus p&aring; uppgiften. St&auml;ll en klocka s&aring; att ni har koll p&aring; n&auml;r ni ska plugga och n&auml;r ni ska ta paus, d&aring; blir det l&auml;ttare att h&aring;lla koncentrationen.</span></li><li class="c3 c5"><span class="c0">Ett bra s&auml;tt att plugga tillsammans &auml;r att turas om att f&ouml;rklara f&ouml;r varandra. Det g&ouml;r att du sj&auml;lv f&aring;r koll p&aring; kunskapen och f&aring;r koll p&aring; vad du kan och vad du beh&ouml;ver l&auml;ra dig mer om.</span></li></ol>'),
('Kommunikation när du samarbetar','Samarbeta','<p class="c13"><span>Att l&auml;ra sig i grupp kan vara s&aring; roligt och givande </span><span class="c4">f&ouml;r inl&auml;rningen. Men f&ouml;r att det ska k&auml;nnas b&aring;de roligt och l&auml;rorikt &auml;r det viktigt att gruppen fungerar och framf&ouml;r allt, att alla trivs. H&auml;r &auml;r n&aring;gra saker som &auml;r bra att t&auml;nka p&aring; f&ouml;r att grupparbetet ska bli s&aring; bra som m&ouml;jligt:</span></p><p class="c5"><span class="c4">Bild/filmruta/ljudfil</span></p><p class="c5 c7"><span class="c4"></span></p><p class="c5"><span class="c4">Instruktion f&ouml;r strategin</span></p><p class="c13 c7"><span class="c4"></span></p><ol class="c12 lst-kix_jl5f0o99urdt-0 start" start="1"><li class="c2"><span class="c4">Var mellan 2-5 personer i gruppen, ni ska inte vara fler &auml;n att alla har n&aring;got att g&ouml;ra</span></li><li class="c2"><span class="c4">L&auml;s uppgiften noggrant innan ni b&ouml;rjar</span></li><li class="c2"><span class="c4">Ta reda p&aring; hur arbetet kommer att bed&ouml;mas av er l&auml;rare</span></li><li class="c2"><span class="c4">Skriv ett gruppkontrakt d&auml;r ni kommer &ouml;verens om vad ni ska g&ouml;ra och vem som ska g&ouml;ra vad och n&auml;r varje del ska vara klar</span></li><li class="c2"><span class="c4">S&auml;tt ig&aring;ng. Fokusera p&aring; uppgiften och se till att ni f&ouml;ljer kontraktet</span></li><li class="c2"><span class="c4">Lyssna p&aring; och prata med varandra</span></li><li class="c2"><span class="c4">Satsa p&aring; presentationen &ndash; anv&auml;nd er fantasi och g&ouml;r n&aring;got kul! T&auml;nk p&aring; att de som ska titta p&aring; redovisningen ocks&aring; ska l&auml;ra sig n&aring;got.</span></li></ol>'),
('Att diskutera i grupp','Samarbeta','<p class="c5"><span class="c1">Att arbeta i grupp kan vara s&aring; roligt och givande f&ouml;r inl&auml;rningen. En viktig men ibland sv&aring;r del i alla grupparbeten &auml;r kommunikationen och diskussionerna i gruppen s&aring; att alla gruppmedlemmar kommer till tals.</span></p><p class="c0"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c0 c3"><span class="c1"></span></p><p class="c0"><span class="c1">Instruktion f&ouml;r strategin</span></p><p class="c0 c3"><span class="c1"></span></p><ol class="c9 lst-kix_lrzd9rhvzzdb-0 start" start="1"><li class="c0 c2"><span class="c1">N&auml;r man diskuterar i grupp &auml;r det viktigt att man uttrycker sina egna &aring;sikter- men det &auml;r lika viktigt att man kommer ih&aring;g att lyssna n&auml;r andra talar.</span></li><li class="c0 c2"><span class="c1">&Auml;gna all uppm&auml;rksamhet &aring;t gruppen, st&auml;ng av mobilen och ta bort distraktioner.</span></li><li class="c0 c2"><span class="c1">Dela in taltiden s&aring; alla f&aring;r samma chans att uttrycka sig</span></li><li class="c0 c2"><span class="c1">Det kan du tex g&ouml;ra genom att tilldela varje person i gruppen ett antal minuter. St&auml;ll klockan och n&auml;r tiden &auml;r slut &auml;r det n&auml;sta gruppmedlems tur.</span></li><li class="c0 c2"><span class="c1">Uppmuntra alla att uttrycka sina &aring;sikter</span></li></ol>'),
('Koll på materialet','Planera ditt lärande','<p class="c2"><span class="c1">Allt l&auml;rande st&ouml;ds b&auml;st av att du vet vilken helhet du har att g&ouml;ra med. </span></p><p class="c2"><span class="c1">Allts&aring; beh&ouml;ver du ha en bra &ouml;verblick &ouml;ver hela din studiesituation och det material du beh&ouml;ver ha.</span></p><p class="c2 c4"><span class="c1"></span></p><p class="c2 c4"><span class="c1"></span></p><p class="c2 c4"><span class="c1"></span></p><p class="c2 c4"><span class="c1"></span></p><p class="c2"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c2 c4"><span class="c1"></span></p><p class="c2"><span class="c1">Instruktion f&ouml;r strategin</span></p><ol class="c7 lst-kix_bxgf23d5yhai-0 start" start="1"><li class="c2 c3"><span class="c1">B&ouml;rja med att l&auml;sa arbetsg&aring;ngen, l&auml;randem&aring;len och hur du kommer bli bed&ouml;md p&aring; omr&aring;det.</span></li><li class="c2 c3"><span class="c1">Samla sedan ihop allt material du kommer beh&ouml;va l&auml;sa.</span></li><li class="c2 c3"><span>Titta igenom materialet och f&ouml;rs&ouml;k </span><span class="c0">skaffa en k&auml;nsla f&ouml;</span><span class="c1">r helheten.</span></li><li class="c2 c3"><span class="c1">G&ouml;r sedan tre h&ouml;gar: A-, B- och C-h&ouml;gen.</span></li><li class="c2 c3"><span class="c1">A-h&ouml;gen= absoluth&ouml;gen. I denna h&ouml;g sorterar du in det material du absolut kommer att beh&ouml;va l&auml;sa f&ouml;r att n&aring; dina m&aring;l.</span></li><li class="c2 c3"><span class="c1">B-h&ouml;gen=bordeh&ouml;gen. I B-h&ouml;gen sorterar du in det material du anser att du b&ouml;r l&auml;sa f&ouml;r att n&aring; dina m&aring;l.</span></li><li class="c2 c3"><span class="c1">C-h&ouml;gen=ce la vie-h&ouml;gen. I C-h&ouml;gen sorterar du in det material som du anser att du inte beh&ouml;ver ta del av f&ouml;r att n&aring; dina m&aring;l.</span></li><li class="c2 c3"><span class="c1">N&auml;r du gjort de tre h&ouml;garna beh&ouml;ver du fundera &ouml;ver om det finns annat material att anv&auml;nda f&ouml;r att l&auml;ra dig mer, tex dokument&auml;rer, podcasts osv.</span></li>'),
('Skapa överblick','Planera ditt lärande','<p class="c2"><span class="c0">Att vara f&ouml;rberedd i sitt skolarbete &auml;r viktigt.Och f&ouml;r att kunna vara f&ouml;rberedd beh&ouml;ver du ha en &ouml;verblick &ouml;ver ditt arbete. Detta f&aring;r du genom att g&ouml;ra en tydlig terminsplanering i din loggbok. </span></p><p class="c2"><span style="overflow: hidden; display: inline-block; margin: 0.00px 0.00px; border: 0.00px solid #000000; transform: rotate(0.00rad) translateZ(0px); -webkit-transform: rotate(0.00rad) translateZ(0px); width: 187.05px; height: 229.50px;"><img alt="" src="images/image2.png" style="width: 507.05px; height: 292.48px; margin-left: -93.20px; margin-top: -33.65px; transform: rotate(0.00rad) translateZ(0px); -webkit-transform: rotate(0.00rad) translateZ(0px);" title=""></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2"><span class="c0">Instruktion f&ouml;r strategin</span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><p class="c2 c3"><span class="c0"></span></p><ol class="c5 lst-kix_nggsyaebjck1-0 start" start="1"><li class="c1"><span class="c0">Under varje steg&auml;mne skriver du i vilket steg du ska redovisa och slutf&ouml;ra vilken vecka.</span></li><li class="c1"><span class="c0">Det &auml;r viktigt att du inte redovisar flera steg varje vecka utan sprider ut dina redovisningar p&aring; flera veckor.</span></li><li class="c1"><span class="c0">Under kurs skriver du i vilka kurser du ska l&auml;sa under terminen, n&auml;r de b&ouml;rjar och slutar och n&auml;r eventuella inl&auml;mningar, prov och annat viktigt.</span></li><li class="c1"><span class="c0">Under kommentarsrutan fyller du i andra viktiga h&auml;ndelser, tex om du har nationella prov, studiedagar, lov osv.</span></li><li class="c1"><span class="c0">Nu har du skaffat dig en tydlig &ouml;verblick &ouml;ver ditt skolarbete &ouml;ver hela terminen!</span></li></ol>'),
('Sätta mål - hur du förbereder dig','Planera ditt lärande','<p class="c3"><span class="c0">Hur g&ouml;r du n&auml;r du s&auml;tter upp m&aring;l? Hur hj&auml;lper dina m&aring;l dig att l&auml;ra dig mer?</span></p><p class="c3"><span class="c0">Att s&auml;tta m&aring;l &auml;r en viktig del i skolarbetet. Innan du planerar din vecka och s&auml;tter dina m&aring;l &auml;r det viktigt att du f&ouml;rbereder dig noga. </span></p><p class="c2"><span class="c0"></span></p><p class="c3"><span class="c0">Bild/film</span></p><p class="c2"><span class="c0"></span></p><p class="c2"><span class="c0"></span></p><p class="c2"><span class="c0"></span></p><p class="c2"><span class="c0"></span></p><ol class="c7 lst-kix_9j7wpc44c237-0 start" start="1"><li class="c3 c5"><span class="c0">L&auml;s uppgiften/uppgifterna du ska arbeta med</span></li><li class="c3 c5"><span class="c0">Fundera &ouml;ver hur mycket tid du beh&ouml;ver f&ouml;r att arbeta med uppgiften/uppgifterna och g&ouml;r en tidsuppskattning.</span></li><li class="c3 c5"><span class="c0">&Auml;r det en uppgift som du ska arbeta med sj&auml;lv eller kr&auml;ver uppgiften att du samarbetar med en eller flera kompisar?</span></li><li class="c3 c5"><span class="c0">N&auml;r du har uppskattat hur l&aring;ng tid uppgifterna tar och funderat p&aring; hur du ska arbeta med dem &auml;r det dags att planera i loggboken.</span></li></ol>'),
('TUR-mål','Planera ditt lärande','<p class="c12"><span class="c1">N&auml;r du g&ouml;r din planering i loggboken f&ouml;rs&ouml;k s&auml;tta m&aring;l som &auml;r TUR-m&aring;l just f&ouml;r dig</span></p><ol class="c14 lst-kix_pbt0jrev1qk6-0 start" start="1"><li class="c7 c6"><span class="c1">Med tydliga m&aring;l menas att m&aring;len finns, att de kan m&auml;tas och utv&auml;rderas och att du f&ouml;rst&aring;r dem, dvs att de &auml;r konkreta just f&ouml;r dig. &Auml;r l&auml;randem&aring;let p&aring; Kunskapsporten f&ouml;r l&aring;ngt och komplicerat? F&ouml;rs&ouml;k att konkretisera det s&aring; att det blir tydligt f&ouml;r just dig.</span></li><li class="c7 c6"><span class="c1">Det &auml;r &auml;ven viktigt att dina m&aring;l &auml;r utmanande s&aring; att du l&auml;r dig maximalt. Om m&aring;len &auml;r f&ouml;r enkla kommer du bli uttr&aring;kad - och om m&aring;len &auml;r f&ouml;r sv&aring;ra &auml;r risken stor att du ger upp. </span></li><li class="c6 c7"><span>F&ouml;r att du ska orka g&aring; i skolan och l&auml;ra dig nya saker &auml;r det viktigt att dina m&aring;l &auml;r rimliga.</span><span class="c1">&nbsp;T&auml;nk s&aring;h&auml;r: Du har aldrig sprungit l&auml;ngre &auml;n 3 km men s&auml;tter upp m&aring;let att springa ett marathon om tv&aring; m&aring;nader. Sedan b&ouml;rjar du tr&auml;na men m&auml;rker snabbt att ett marathon just nu &auml;r utom r&auml;ckh&aring;ll f&ouml;r dig och ger upp. Du beh&ouml;ver tr&auml;na mer och under en l&auml;ngre tid f&ouml;r att n&aring; ditt m&aring;l och i nul&auml;get &auml;r det inte s&auml;rskilt rimligt. S&auml;tt hellre ett m&aring;l som du k&auml;nner att du kan lyckas med, t.ex att springa 5 km. N&auml;r du v&auml;l n&aring;tt det m&aring;let kan du s&auml;tta 1 mil och kanske s&aring; sm&aring;ningom sikta mot ett marathon</span></li></ol>'),
('Strategier för att komma igång','Planera ditt lärande','<p class="c4"><span class="c0">Ibland kan det vara sv&aring;rt att komma ig&aring;ng med ett skolarbete. Det kan bero p&aring; m&aring;nga olika saker, till exempel att du inte har planerat ditt arbete med tydliga m&aring;l eller att det &auml;r saker omkring som st&ouml;r dig. </span></p><p class="c1"><span class="c0"></span></p><p class="c4"><span class="c0">Det finns dock bra strategier f&ouml;r att undvika &ldquo;sv&aring;rt att komma ig&aring;ng-f&auml;llan&rdquo;</span></p><p class="c1"><span class="c0"></span></p><p class="c1"><span class="c0"></span></p><p class="c4"><span class="c0">Bild/Film</span></p><p class="c1"><span class="c0"></span></p><p class="c4"><span class="c0">Instruktion:</span></p><p class="c1"><span class="c0"></span></p><p class="c1"><span class="c0"></span></p><ol class="c7 lst-kix_w07g896galh2-0 start" start="1"><li class="c3 c2"><span class="c0">Den f&ouml;rsta och kanske viktigaste strategin &auml;r att inte k&auml;nna efter s&aring; mycket. F&ouml;r g&ouml;r du det &auml;r risken stor att du b&ouml;rjar med n&aring;got annat. </span></li><li class="c3 c2"><span class="c5">St&auml;ng av ?</span><span class="c0">och plocka bort allt som g&ouml;r att du inte kan h&aring;lla fokus.</span></li><li class="c3 c2"><span class="c0">G&ouml;r en lista med det du ska g&ouml;ra och dela upp uppgiften i mindre delm&aring;l. </span></li><li class="c3 c2"><span>T&auml;nk ocks&aring; p&aring; att g&ouml;ra delm&aring;len s&aring; konkreta och tydliga som m&ouml;jligt.</span></li><li class="c3 c2"><span class="c0">Var sn&auml;ll mot dig sj&auml;lv och b&ouml;rja med n&aring;got som du tycker &auml;r enkelt s&aring; att du faktiskt kommer ig&aring;ng.</span></li><li class="c3 c2"><span class="c0">Om du m&auml;rker att ta det &auml;r sv&aring;rt att h&aring;lla koncentrationen l&auml;ngre stunder kan du st&auml;lla klockan p&aring; exempelvis 25 minuter och d&auml;refter ta en kort paus.</span></li><li class="c2 c3"><span class="c0">P&aring;b&ouml;rja uppgiften. Du m&aring;ste inte bli helt f&auml;rdig. Att iallafall ha p&aring;b&ouml;rjat en uppgift &auml;r inte heller s&aring; tokigt och det g&ouml;r det l&auml;ttare att forts&auml;tta n&auml;sta g&aring;ng. </span></li></ol>'),
('Enkel inlärningscykel','Planera ditt lärande','<p class="c2"><span class="c0">Om du vill f&aring; b&auml;ttre koll p&aring; hur du l&auml;r dig p&aring; ett mer strukturerat s&auml;tt s&aring; pr&ouml;va det h&auml;r: Planera</span></p><p class="c2"><span class="c0">Genomf&ouml;ra</span></p><p class="c2"><span class="c0">Kontrollera</span></p><p class="c2"><span class="c0">Reflektera </span></p><p class="c1"><span class="c0"></span></p><p class="c2"><span class="c0">Allt ihop &auml;r &aring;tg&auml;rder som kan anv&auml;ndas f&ouml;r att g&ouml;ra inl&auml;rningsprocessen mer effektiv. </span></p><p class="c1"><span class="c0"></span></p><p class="c1"><span class="c0"></span></p><p class="c2"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1"><span class="c0"></span></p><p class="c2"><span class="c0">Instruktion f&ouml;r strategin</span></p><p class="c1"><span class="c0"></span></p><ol class="c5 lst-kix_plg50vndgift-0 start" start="1"><li class="c2 c3"><span class="c0">Planera</span></li></ol><ol class="c5 lst-kix_plg50vndgift-1 start" start="1"><li class="c2 c7"><span class="c0">Ett s&auml;tt att att jobba mer strukturerat &auml;r att l&aring;ngsiktigt planera genom att skapa en studieplan/terminsplanering. Vilka m&aring;l ska du n&aring;? Hur ska du n&aring; dit p&aring; b&auml;sta s&auml;tt? Vilka uppgifter beh&ouml;ver g&ouml;ras? Vilka strategier fungerar b&auml;st f&ouml;r dig? </span></li></ol><ol class="c5 lst-kix_plg50vndgift-0" start="2"><li class="c2 c3"><span class="c0">Genomf&ouml;ra</span></li></ol><ol class="c5 lst-kix_plg50vndgift-1 start" start="1"><li class="c2 c7"><span class="c0">Det r&auml;cker ju inte med att bara planera. Man beh&ouml;ver ju ocks&aring; genomf&ouml;ra planerade uppgifter. Skriv arbetsm&aring;l i loggboken. G&ouml;r uppgifterna. Bocka av n&auml;r du gjort f&auml;rdigt. </span></li></ol><ol class="c5 lst-kix_plg50vndgift-0" start="3"><li class="c2 c3"><span class="c0">Kontrollera</span></li></ol><ol class="c5 lst-kix_plg50vndgift-1 start" start="1"><li class="c2 c7"><span class="c0">Om du vill bli mer effektiv med ditt l&auml;rande beh&ouml;ver du ocks&aring; kontrollera resultatet. L&ouml;ste du uppgifterna enligt plan? Vad blev resultatet? </span></li></ol><ol class="c5 lst-kix_plg50vndgift-0" start="4"><li class="c2 c3"><span class="c0">Reflektera</span></li></ol><ol class="c5 lst-kix_plg50vndgift-1 start" start="1"><li class="c2 c7"><span class="c0">Det r&auml;cker inte med att kolla resultatet. Du beh&ouml;ver ocks&aring; reflektera &ouml;ver din plan och justera vid behov. H&ouml;ll planen? om inte vad beh&ouml;ver du &auml;ndra tills n&auml;sta g&aring;ng?</span></li></ol>'),
('Dela upp i underuppgifter','Planera ditt lärande','<p class="c1"><span class="c2">Ibland kan det vara bra att g&ouml;ra en plan f&ouml;r hur du ska ta dig an en uppgift. Och om det &auml;r en stor uppgift som str&auml;cker sig &ouml;ver ett par veckor &auml;r det kanske n&ouml;dv&auml;ndigt. Annars finns det en risk att du skjuter p&aring; uppgiften till sista stund och pl&ouml;tsligt k&auml;nns det som att du har f&ouml;r mycket att g&ouml;ra.</span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1"><span class="c2">Varje uppgift p&aring; Kunskapsporten har instruktioner hur man ska g&ouml;ra uppgiften. Dessa kan du anv&auml;nda f&ouml;r att skapa underuppgifter i din planering. H&auml;r &auml;r ett exempel fr&aring;n ett steg i franska:</span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1"><span style="overflow: hidden; display: inline-block; margin: 0.00px 0.00px; border: 0.00px solid #000000; transform: rotate(0.00rad) translateZ(0px); -webkit-transform: rotate(0.00rad) translateZ(0px); width: 602.00px; height: 504.00px;"><img alt="" src="images/image3.png" style="width: 602.00px; height: 504.00px; margin-left: 0.00px; margin-top: 0.00px; transform: rotate(0.00rad) translateZ(0px); -webkit-transform: rotate(0.00rad) translateZ(0px);" title=""></span></p><p class="c1"><span class="c2">&nbsp;</span></p><p class="c1"><span class="c2">Genom att trycka p&aring; pennan n&auml;r du lagt in uppgiften i din loggbok kan du l&auml;gga in underuppgifter till uppgiften:</span></p><p class="c1"><span style="overflow: hidden; display: inline-block; margin: 0.00px 0.00px; border: 0.00px solid #000000; transform: rotate(0.00rad) translateZ(0px); -webkit-transform: rotate(0.00rad) translateZ(0px); width: 497.50px; height: 485.11px;"><img alt="" src="images/image2.png" style="width: 497.50px; height: 485.11px; margin-left: 0.00px; margin-top: 0.00px; transform: rotate(0.00rad) translateZ(0px); -webkit-transform: rotate(0.00rad) translateZ(0px);" title=""></span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1"><span class="c2">Och n&auml;r du har lagt in dina underuppgifter fr&aring;n instruktionerna i uppgiften och tryckt p&aring; spara kommer det se ut s&aring; h&auml;r i din loggbok. Nu kan du bocka av momenten i den takt du klarar av dem och uppgiften k&auml;nns kanske inte lika stor l&auml;ngre.</span></p>'),
('Använd dina kunskaper om världen','Lyssna','<p class="c6"><span>N&auml;r du anv&auml;nder </span><span class="c2">olika strategier f&ouml;r att lyssna blir det enklare f&ouml;r dig att f&ouml;rst&aring; och l&ouml;sa problem n&auml;r spr&aring;kkunskaperna inte r&auml;cker till. Denna strategi l&auml;r dig att anv&auml;nda dina kunskaper om v&auml;rlden n&auml;r du &ouml;var p&aring; h&ouml;rf&ouml;rst&aring;else.</span></p><p class="c1"><span class="c2"></span></p><p class="c0"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c0 c3"><span class="c2"></span></p><p class="c0 c3"><span class="c2"></span></p><p class="c6"><span class="c2">Instruktion f&ouml;r strategin:</span></p><p class="c0"><span class="c2">N&auml;r du tex ska g&ouml;ra en h&ouml;r&ouml;vning eller f&ouml;rs&ouml;ka f&ouml;rst&aring; n&aring;gon som pratar ett annat spr&aring;k kan du:</span></p><p class="c0 c8 c3"><span class="c2"></span></p><ul class="c10 lst-kix_s89y08h3h2wi-0 start"><li class="c0 c7"><span class="c2">Uppm&auml;rksamma siffror och platser p&aring; ort/stad/land</span></li><li class="c0 c7"><span class="c2">Anv&auml;nda din kunskap om v&auml;rlden f&ouml;r att l&auml;ttare f&ouml;rst&aring;. Om du tex har varit i Frankrike och vet en del om landet kommer du l&auml;ttare att kunna f&ouml;rst&aring; det som s&auml;gs och s&auml;tta det i ett sammanhang.</span></li></ul>'),
('Förutspå och lyssna flera gånger','Lyssna','<span>N&auml;r du ska &ouml;va p&aring; h&ouml;rf&ouml;rst&aring;else genom att tex lyssna p&aring; ett nyhetsprogram eller titta p&aring; ett klipp p&aring; engelska eller moderna spr&aring;k &auml;r det l&auml;tt att hamna i vad du </span><span class="c13">inte </span><span class="c0">f&ouml;rst&aring;r</span></li><li class="c4 c10"><span class="c0">Denna strategi ger dig verktyg f&ouml;r att fokusera p&aring; det du faktiskt f&ouml;rst&aring;r.</span></li><li class="c4 c10 c11"><span class="c0"></span></li></ul><p class="c4"><span class="c0">Bild/filmruta/ljudfil</span></p><ul class="c1 lst-kix_6ej0ppjf0h39-0"><li class="c4 c9"><span class="c0"></span></li></ul><p class="c4"><span class="c0">Instruktion f&ouml;r strategin</span></p><ol class="c1 lst-kix_52wgcm615ib3-0 start" start="1"><li class="c6"><span>Innan du b&ouml;rjar lyssna ska du</span><span class="c0">&nbsp;gissa vad programmet kommer handla om. Om det &auml;r ett nyhetsprogram kan du gissa p&aring; &auml;mnen som varit aktuella under tiden, om det &auml;r en podd om vad &auml;mnet handlar om. </span></li><li class="c6"><span class="c0">Skriv ner lite st&ouml;dord. Genom detta s&auml;tt aktiverar du f&ouml;rm&aring;gan att f&ouml;rutsp&aring;.</span></li><li class="c6"><span class="c0">Lyssna sedan en g&aring;ng och &auml;ndra och stryk fr&aring;n st&ouml;dorden f&ouml;r att l&auml;gga till ny information.</span></li><li class="c6"><span class="c0">Prata sedan med en kompis om inneh&aring;llet fr&aring;n st&ouml;dorden och l&auml;gg till den information som du missat men som kompisen har uppfattat.</span></li><li class="c6"><span class="c0">Lyssna en g&aring;ng till.</span></li><li class="c6"><span class="c0">Efter andra lyssningen g&aring;r du och kompisen gemensamt igenom vad det handlade om och benar i vad som v&aring;llade problem. Det kan r&ouml;ra sig om ord eller vad inslaget handlar om. Hj&auml;lp varandra att f&ouml;rst&aring; och hur ni kan tolka budskap eller betydelse av ord.</span></li><li class="c6"><span class="c0">Lyssnar sedan en tredje g&aring;ng och fokusera p&aring; om det finns ytterligare information som inte du snappat upp f&ouml;rsta g&aring;ngen.</span></li><li class="c6"><span class="c0">Sist ska du reflektera &ouml;ver vad som fick dig att f&ouml;rst&aring; och hur du i framtiden ska g&aring; tillv&auml;ga n&auml;r du &ouml;var h&ouml;rf&ouml;rst&aring;else.</span></li></ol>'),
('Lyssna aktivt på en föreläsning','Lyssna','<p class="c5"><span class="c2">Ett aktivt lyssnande betyder att vi lyssnar f&ouml;r att f&ouml;rst&aring; n&aring;got, inte f&ouml;r att kunna svara eller fylla i vad n&aring;gon annan har sagt.</span></p><p class="c5"><span class="c2">N&auml;r du lyssnar aktivt fokuserar du p&aring; vad hela personen du lyssnar p&aring; ber&auml;ttar, b&aring;de det som s&auml;gs men &auml;ven kroppsspr&aring;k och tonfall.</span></p><p class="c3"><span class="c2"></span></p><p class="c3"><span class="c2"></span></p><p class="c5"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c3"><span class="c2"></span></p><p class="c3"><span class="c2"></span></p><p class="c5"><span class="c2">Instruktion f&ouml;r strategin:</span></p><p class="c5"><span class="c2">Innan du g&aring;r p&aring; &nbsp;f&ouml;rel&auml;sningen &auml;r det viktigt att du skaffar dig en &ouml;verblick &ouml;ver &auml;mnet genom att l&auml;sa lite bakgrundsinformation. Vad kommer f&ouml;rel&auml;sningen att handla om? Om du redan innan har lite f&ouml;rf&ouml;rst&aring;else kommer du enklare att kunna lyssna aktivt.</span></p><p class="c3"><span class="c2"></span></p><p class="c5"><span class="c2">Under f&ouml;rel&auml;sningen</span></p><ol class="c1 lst-kix_d83oorj9ne9b-0 start" start="1"><li class="c4"><span class="c2">Koncentrera dig p&aring; att lyssna, det &auml;r viktigare att f&ouml;lja resonemanget under en f&ouml;rel&auml;sning &auml;n att slaviskt anteckna allt som l&auml;raren s&auml;ger.</span></li><li class="c4"><span class="c2">Leta efter v&auml;gledning, tex nyckelord och begrepp som l&auml;raren anv&auml;nder.</span></li><li class="c4"><span class="c2">Det &auml;r enklare att lyssna effektivt om du har en klar sikt och inga distraktioner s&aring; hLyssna efter det du redan kanitta d&auml;rf&ouml;r en bra sittplats s&aring; att du ser tavlan, sk&auml;rmen och l&auml;raren.</span></li></ol>'),
('Lyssna efter det du redan kan','Lyssna','<p class="c8"><span class="c1">N&auml;r du anv&auml;nder olika strategier f&ouml;r att lyssna blir det enklare f&ouml;r dig att f&ouml;rst&aring; och l&ouml;sa problem n&auml;r spr&aring;kkunskaperna inte r&auml;cker till. Denna strategi l&auml;r dig att anv&auml;nda det du redan kan i spr&aring;ket n&auml;r du &ouml;var p&aring; h&ouml;rf&ouml;rst&aring;else.</span></p><p class="c8 c12"><span class="c1"></span></p><p class="c8 c12"><span class="c1"></span></p><p class="c0"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c0 c12"><span class="c1"></span></p><p class="c0 c12"><span class="c1"></span></p><h3 class="c9" id="h.msev7g4b03re"><span class="c2">Instruktion f&ouml;r strategin:</span></h3><p class="c0"><span class="c1">N&auml;r du tex ska g&ouml;ra en h&ouml;r&ouml;vning eller f&ouml;rs&ouml;ka f&ouml;rst&aring; n&aring;gon som pratar ett annat spr&aring;k kan du:</span></p><ul class="c14 lst-kix_ca1vq7ia0py4-0 start"><li class="c0 c15"><span class="c1">Lyssna efter ord som du redan kan och k&auml;nner igen f&ouml;r att &ouml;ka din f&ouml;rst&aring;else av det som s&auml;gs.</span></li><li class="c0 c15"><span class="c1">Lyssna efter internationella ord som du tex f&ouml;rst&aring;r p&aring; engelska eller p&aring; andra spr&aring;k. </span></li></ul>'),
('Någon som lyssnar','Lyssna','<p class="c2"><span class="c1">En bra strategi f&ouml;r att minnas det du l&auml;st &auml;r att sammanfatta varje stycke av texten och sedan prata om det du har l&auml;st.</span></p><p class="c2 c4"><span class="c1"></span></p><p class="c2 c4"><span class="c1"></span></p><p class="c2"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c2 c4"><span class="c1"></span></p><p class="c2"><span class="c1">Instruktion f&ouml;r strategin</span></p><ol class="c8 lst-kix_5rskj4yto1v3-0 start" start="1"><li class="c0"><span class="c1">L&auml;s texten</span></li><li class="c0"><span class="c1">Sammanfatta varje stycke eller kapitel genom att ta korta anteckningar</span></li><li class="c0"><span class="c1">Prata sedan om det du l&auml;st genom att be en kompis att lyssna.</span></li><li class="c0"><span class="c1">Den som lyssnar ska helst inte l&auml;gga sig i och avbryta det du s&auml;ger, utan bara vara ett &ouml;ra som motiverar dig att forts&auml;tta tala. </span></li></ol>'),
('Skapa bilder i huvudet','Lyssna','<p class="c8"><span>N&auml;r du anv&auml;nder olika strategier f&ouml;r att lyssna blir det enklare f&ouml;r dig att f&ouml;rst&aring; och l&ouml;sa problem n&auml;r spr&aring;kkunskaperna inte r&auml;cker till. Denna strategi l&auml;r dig att skapa bilder i huvudet n&auml;r du &ouml;var h&ouml;rf&ouml;rst&aring;else.</span></p><p class="c1"><span class="c0"></span></p><p class="c4"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c2"><span class="c0"></span></p><p class="c2"><span class="c0"></span></p><p class="c8"><span class="c0">Instruktion f&ouml;r strategin:</span></p><p class="c4"><span class="c0">N&auml;r du tex ska g&ouml;ra en h&ouml;r&ouml;vning eller f&ouml;rs&ouml;ka f&ouml;rst&aring; n&aring;gon som pratar ett annat spr&aring;k kan du:</span></p><p class="c2 c13"><span class="c0"></span></p><ol class="c3 lst-kix_2o3dexxq96su-0 start" start="1"><li class="c4 c9"><span class="c0">F&ouml;rs&ouml;k att t&auml;nka i huvudet och se bilder fr&aring;n den situation/h&auml;ndelse du lyssnar p&aring;. Hur ser det ut? Vilka personer &auml;r med? Hur m&aring;nga?</span></li><li class="c4 c9"><span class="c0">T&auml;nk p&aring; vad du vill ha reda p&aring;, vad ber&auml;ttas? Vem ber&auml;ttas det om?</span></li></ol>'),
('Anteckningar','Lära nytt','<p class="c1"><span class="c2">Du tar hela tiden till dig ny kunskap. Din inl&auml;rning kan bli betydligt enklare om du arbetar p&aring; ett effektivt s&auml;tt. Det kan vara bra att anteckna samtidig som du l&auml;ser, lyssnar, kollar eller diskuterar. Anteckningar hj&auml;lper dig att f&ouml;rst&aring; och komma ih&aring;g.</span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1 c3"><span class="c2"></span></p><p class="c1"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c1 c3"><span class="c2"></span></p><ol class="c6 lst-kix_wd6kraxhlk3u-0 start" start="1"><li class="c0"><span class="c2">T&auml;nk p&aring; att anteckningarna &auml;r dina, de &auml;r till f&ouml;r dig. Du ska f&ouml;rst&aring; dem och ha nytta av dem. Skriv med egna ord.</span></li><li class="c0"><span class="c2">Skriv inte mer &auml;n du beh&ouml;ver. Skriv st&ouml;dord, listor, tankekartor, anv&auml;nd olika f&auml;rger, rita bilder och symboler. Stryk under det som du anser vara viktigt. </span></li><li class="c0"><span class="c2">G&aring; igenom dina anteckningar, repetera. Skriv eventuellt rent dem.</span></li><li class="c0"><span class="c2">Ber&auml;tta g&auml;rna om &auml;mnesomr&aring;det f&ouml;r en kompis, familjemedlem eller en p&aring;hittad publik.</span></li></ol>'),
('Anteckningar 2','Lära nytt','<p class="c1"><span class="c2">Du tar hela tiden till dig ny kunskap. Din inl&auml;rning kan bli betydligt enklare om du arbetar p&aring; ett effektivt s&auml;tt. Det kan vara bra att anteckna samtidig som du l&auml;ser, lyssnar, kollar eller diskuterar. Anteckningar hj&auml;lper dig att f&ouml;rst&aring; och komma ih&aring;g. </span></p><p class="c0"><span class="c2"></span></p><p class="c0"><span class="c2"></span></p><p class="c1"><span class="c2">Bild/filmruta/ljudfil</span></p><p class="c0"><span class="c2"></span></p><ol class="c5 lst-kix_ocx9hb892v73-0 start" start="1"><li class="c1 c4"><span class="c2">L&auml;s dina anteckningar f&ouml;r att repetera.</span></li><li class="c1 c4"><span class="c2">Skriv om anteckningar till ett fr&aring;ga-och-svar-format.</span></li><li class="c1 c4"><span class="c2">St&auml;ll fr&aring;gorna till kamrat, familjemedlem eller p&aring;hittad publik. </span></li></ol>'),
('Begreppsträning Lappar','Lära nytt','<p class="c3"><span class="c0">Du tar hela tiden till dig ny kunskap. Din inl&auml;rning kan bli betydligt enklare om du arbetar p&aring; ett effektivt s&auml;tt. N&auml;r du l&auml;ser eller lyssnar och det visar sig inneh&aring;lla ord och begrepp som du inte &auml;r s&auml;ker p&aring; &auml;r det bra att g&ouml;ra s&aring; h&auml;r:</span></p><p class="c2"><span class="c0"></span></p><p class="c2"><span class="c0"></span></p><p class="c3"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c2"><span class="c0"></span></p><ol class="c4 lst-kix_os6lru4315wk-0 start" start="1"><li class="c3 c5"><span class="c0">Skriv upp de nya begreppen som du inte k&auml;nner igen p&aring; en post-it lapp.</span></li><li class="c3 c5"><span class="c0">Skriv en f&ouml;rklaring (se till f&ouml;rklaringen &auml;r r&auml;tt och s&aring; kortfattad som m&ouml;jligt).</span></li><li class="c3 c5"><span class="c0">H&auml;ng upp post-it lapparna p&aring; st&auml;llen d&auml;r du r&ouml;r dig mycket (badrumsspegeln, kylsk&aring;pet, inuti ditt sk&aring;p&hellip;)</span></li><li class="c3 c5"><span class="c0">L&auml;s p&aring; lapparna s&aring; ofta du ser dem.</span></li></ol>'),
('Begreppsträning Memory','Lära nytt','<p class="c1"><span class="c0">Du tar hela tiden till dig ny kunskap. Din inl&auml;rning kan bli betydligt enklare om du arbetar p&aring; ett effektivt s&auml;tt. N&auml;r du l&auml;ser eller lyssnar och det visar sig inneh&aring;lla ord och begrepp som du inte &auml;r s&auml;ker p&aring; &auml;r det bra att g&ouml;ra s&aring; h&auml;r:</span></p><p class="c1 c3"><span class="c0"></span></p><p class="c1 c3"><span class="c0"></span></p><p class="c1"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1 c3"><span class="c0"></span></p><ol class="c8 lst-kix_pxka5axfp8r4-0 start" start="1"><li class="c1 c2"><span class="c0">Skriv upp de nya begreppen som du inte k&auml;nner igen p&aring; en lapp.</span></li><li class="c1 c2"><span class="c0">Skriv en f&ouml;rklaring p&aring; en likadan lapp (se till f&ouml;rklaringen &auml;r r&auml;tt och s&aring; kortfattad som m&ouml;jligt).</span></li><li class="c1 c2"><span class="c0">Para ihop lapparna eller varf&ouml;r inte fr&aring;ga n&aring;gon om de vill spela memory. </span></li></ol>'),
('Bli bättre på att förstå ett nytt ämnesinnehåll','Lära nytt','<p class="c6"><span class="c1">Ibland &auml;r det sv&aring;rt att f&ouml;rst&aring; ett nytt &auml;mnesinneh&aring;ll. Till exempel att f&ouml;rst&aring; tidsperioden ren&auml;ssansen i &auml;mnet historia eller att f&ouml;rst&aring; vad ett kretslopp &auml;r i &auml;mnet biologi. </span></p><p class="c6"><span class="c1">Hur kan du g&ouml;ra f&ouml;r att f&ouml;rst&aring; det nya? H&auml;r &auml;r n&aring;gra smarta tips. </span></p><p class="c0"><span class="c1"></span></p><ol class="c2 lst-kix_30mai2ejvmoo-0 start" start="1"><li class="c3"><span class="c7">G&ouml;r anteckningar med egna ord. </span></li></ol><p class="c6"><span class="c1">L&auml;s texter om det nya inneh&aring;llet. Lyssna p&aring; f&ouml;rel&auml;sning, g&ouml;r uppgifter. F&ouml;rs&ouml;k sedan att skriva ner och f&ouml;rklara det nya inneh&aring;llet med egna ord. Hitta p&aring; att du ska f&ouml;rklara f&ouml;r en kompis. Skriv ner tydligt s&aring; att du sj&auml;lv f&ouml;rst&aring;r vad du menar. </span></p><ol class="c2 lst-kix_30mai2ejvmoo-0" start="2"><li class="c3"><span class="c7">J&auml;mf&ouml;r anteckningar med v&auml;nner och kompisar i basgruppen</span></li></ol><p class="c6"><span class="c1">Byt anteckningar med n&aring;gon kompis. F&ouml;rs&ouml;k f&ouml;rst&aring; vad texten ber&auml;ttar. Ge lite &aring;terkoppling utifr&aring;n metoden: tv&aring; stj&auml;rnor och en gl&ouml;dlampa. S&auml;g tv&aring; saker som kompisen har gjort bra, till exempel n&aring;got stycke i texten som &auml;r l&auml;tt att f&ouml;rst&aring;. S&auml;g sedan en sak som skulle g&ouml;ra anteckningarna &auml;nd&aring; b&auml;ttre. Till exempel att n&aring;got beh&ouml;ver f&ouml;rklaras &auml;nd&aring; tydligare. </span></p><ol class="c2 lst-kix_30mai2ejvmoo-0" start="3"><li class="c3"><span class="c7">Skriv en ber&auml;ttelse utifr&aring;n dina anteckningar. </span></li></ol><p class="c6"><span class="c1">Om du vill ta det ett steg l&auml;ngre, skriv en ber&auml;ttelse utifr&aring;n dina anteckningar. L&auml;gg till bilder som f&ouml;rtydligar din ber&auml;ttelse. </span></p><ol class="c2 lst-kix_30mai2ejvmoo-0" start="4"><li class="c3"><span class="c7">V&aring;ga fr&aring;ga!</span></li></ol><p class="c6"><span class="c1">Ibland kan man tro att det inte &auml;r bra att fr&aring;ga, att man verkar dum. Det &auml;r helt fel. Det b&auml;sta s&auml;ttet att l&auml;ra sig nytt &auml;r att fr&aring;ga!. Fr&aring;ga l&auml;raren, fr&aring;ga f&ouml;r&auml;ldrar, fr&aring;ga kompisar. Det sv&aring;raste ibland kan vara att v&aring;ga, men fatta mod och fr&aring;ga p&aring;.</span></p>'),
('Inför ett nytt arbetsområde','Lära nytt','<p class="c1 c3"><span class="c0">Du tar hela tiden till dig ny kunskap. Din inl&auml;rning kan bli betydligt enklare om du f&ouml;rbereder dig p&aring; ett effektivt s&auml;tt. H&auml;r kommer tips p&aring; hur du kan g&ouml;ra n&auml;r du ska l&auml;ra nytt i skolan. </span></p><p class="c1 c3 c4"><span class="c0"></span></p><p class="c1 c3"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1 c3 c4"><span class="c0"></span></p><ol class="c5 lst-kix_akzovpfxtumf-0 start" start="1"><li class="c2 c1"><span class="c0">L&auml;s p&aring; innan du g&aring;r till lektionen eller kolla p&aring; ett klipp. </span></li><li class="c2 c1"><span class="c0">F&ouml;rs&ouml;k att f&aring; en &ouml;verblick &ouml;ver vad lektionen/arbetsomr&aring;det kommer handla om.</span></li><li class="c1 c2"><span class="c0">Skriv ned dina tankar eller &aring;sikter om &auml;mnet. </span></li><li class="c2 c1"><span class="c0">Anteckna eventuella fr&aring;gor som dyker upp. </span></li></ol>'),
('Summera det du har lärt dig','Lära nytt','<p class="c3"><span class="c4">N&auml;r man l&auml;r sig ett nytt &auml;mnesomr&aring;de &auml;r det mycket som ska tas in och processas. Ibland kan man tro att man kan n&aring;got b&auml;ttre &auml;n vad man g&ouml;r. Att ha en sammanfattningsbok kan hj&auml;lpa dig att f&aring; syn p&aring; vad du kan - och inte kan&hellip;</span></p><p class="c0"><span class="c4"></span></p><p class="c3"><span class="c4">Instruktion till strategin:</span></p><ul class="c1 lst-kix_xyx4wd6o1z1l-0 start"><li class="c2"><span class="c4">Ta fram en yta som du kan samla dina anteckningar i. Det kan vara anteckningsf&auml;ltet p&aring; Porten eller i en separat anteckningsbok. </span></li><li class="c2"><span class="c4">N&auml;r du har l&auml;st om ett nytt omr&aring;de, sammanfatta det viktigaste med egna ord. Kolla sedan med texten att du faktiskt har skrivit r&auml;tt.</span></li><li class="c2"><span class="c4">Kolla ocks&aring; om det &auml;r n&aring;got som du har gl&ouml;mt att ta med i din sammanfattning och l&auml;gg till det.</span></li><li class="c2"><span class="c4">Din anteckningsbok/-f&auml;lt kan du ocks&aring; anv&auml;nda n&auml;r du pluggar inf&ouml;r prov eller redovisning.</span></li></ul><p class="c0"><span class="c4"></span></p><p class="c3"><span class="c4">Alternativa verktyg f&ouml;r att sammanfatta:</span></p><ul class="c1 lst-kix_b4ge3zgo80h3-0 start"><li class="c2"><span class="c4">skapa en video</span></li><li class="c2"><span class="c4">spela in en podcast</span></li><li class="c2"><span class="c4">g&ouml;r en affisch</span></li></ul>'),
('Att läsa mellan raderna','Läsa','<p class="c0"><span class="c1">Att l&auml;sa mellan raderna inneb&auml;r att man l&auml;ser n&aring;got som f&ouml;rfattaren inte s&auml;ger rakt ut, ett slags detektivarbete d&auml;r du som l&auml;sare samlar ledtr&aring;dar f&ouml;r att f&ouml;rst&aring; vad som h&auml;nder i texten.</span></p><p class="c0 c4"><span class="c1"></span></p><p class="c0"><span class="c1">Du hittar ledtr&aring;dar fr&aring;n:</span></p><ul class="c3 lst-kix_4mhnpz9dtqh7-0 start"><li class="c0 c5"><span class="c1">andra st&auml;llen i texten.</span></li><li class="c0 c5"><span class="c1">fr&aring;n egna erfarenheter och f&ouml;rkunskaper.</span></li></ul><p class="c0 c4"><span class="c1"></span></p><p class="c0"><span class="c1">Du kan tr&auml;na upp din f&ouml;rm&aring;ga att l&auml;sa mellan raderna genom att:</span></p><ul class="c3 lst-kix_y1snwr5y3edd-0 start"><li class="c0 c5"><span class="c1">samtala om texten (innan, under och efter l&auml;sningen).</span></li><li class="c0 c5"><span class="c1">aktivera dina f&ouml;rkunskaper, fundera i f&ouml;rv&auml;g om texten, vad vet du? </span></li><li class="c0 c5"><span class="c1">st&auml;lla fr&aring;gor till texten.</span></li><li class="c0 c5"><span class="c1">reda ut oklarheter.</span></li><li class="c0 c5"><span class="c1">sammanfatta inneh&aring;llet i texten.</span></li></ul><p class="c0 c4"><span class="c1"></span></p><p class="c0"><span class="c1">Sv&aring;rt? </span></p><p class="c0"><span class="c1">Prova att g&ouml;ra s&aring; h&auml;r:</span></p><ol class="c3 lst-kix_5f6qea3jfs4z-0 start" start="1"><li class="c0 c5"><span class="c1">L&auml;s inte hela texten sikta ist&auml;llet in dig p&aring; n&aring;gra ord g&auml;rna substantiv fr&aring;n olika st&auml;llen i texten. </span></li><li class="c0 c5"><span class="c1">Skriv upp orden du valt.</span></li><li class="c0 c5"><span class="c1">Titta p&aring; ett ord i taget, skriv upp de tre f&ouml;rsta sakerna du t&auml;nker p&aring; d&aring; du l&auml;ser ordet.</span></li><li class="c0 c5"><span class="c1">Fundera p&aring; vad det du skrivit upp betyder f&ouml;r dig.</span></li><li class="c0 c5"><span class="c1">L&auml;s nu hela texten och klura ut om du kan anv&auml;nda dina funderingar.</span></li></ol>'),
('Att sammanfatta-skriftligt','Läsa','<p class="c3"><span class="c0">Genom att sammanfatta en text kontrollerar du din egen f&ouml;rst&aring;else av texten. Det inneb&auml;r att du plockar ut det viktigaste ur texten och formulerar det med egna ord.</span></p><p class="c3 c7"><span class="c0"></span></p><p class="c3"><span class="c0">S&aring; h&auml;r kan du g&ouml;ra:</span></p><ol class="c6 lst-kix_o085hwqeu0yw-0 start" start="1"><li class="c2"><span class="c4">Stryk under nyckelord som oftast &auml;r substantiv och verb</span><span class="c4">, anv&auml;nd g&auml;rna till&auml;gget </span><span class="c4 c8">Texthelp Study Skills</span><span class="c0">&nbsp;f&ouml;r att f&auml;rgmarkera nyckelorden i googledokument.</span></li><li class="c2"><span class="c0">Dela upp texten i olika avsnitt</span></li><li class="c2"><span class="c4">G&ouml;r en tankekarta eller en lista med nyckelorden och </span><span class="c0">viktiga begrepp</span></li><li class="c2"><span class="c0">L&auml;gg ifr&aring;n dig ursprungstexten och skriv en egen sammanfattning med nyckelorden och begreppen</span></li><li class="c2"><span class="c0">Anv&auml;nd g&auml;rna f&auml;rger f&ouml;r att markera orden</span></li></ol>'),
('Att sammanfatta genom illustrationer','Läsa','<p class="c3"><span class="c0">Genom att sammanfatta en text kontrollerar du din egen f&ouml;rst&aring;else av texten. Det inneb&auml;r att du plockar ut det viktigaste ur texten och &aring;terger det.</span></p><p class="c3 c4"><span class="c0"></span></p><p class="c3"><span class="c0">S&aring; h&auml;r kan du g&ouml;ra:</span></p><ol class="c10 lst-kix_mrewemczmkgi-0 start" start="1"><li class="c2"><span class="c6">Stryk under nyckelord som oftast &auml;r substantiv och verb, anv&auml;nd g&auml;rna till&auml;gget </span><span class="c5">Texthelp Study Skills</span><span class="c0">&nbsp;f&ouml;r att f&auml;rgmarkera nyckelorden i googledokument.</span></li><li class="c2"><span class="c0">Dela upp texten i olika avsnitt</span></li><li class="c2"><span class="c0">G&ouml;r en tankekarta eller en lista med nyckelorden och viktiga begrepp</span></li><li class="c2"><span class="c0">L&auml;gg ifr&aring;n dig ursprungstexten och rita en bildserie utifr&aring;n tankekartan. Streckgubbar eller konstverk, det spelar ingen roll. </span></li><li class="c2"><span class="c0">L&aring;t n&aring;gon se din bildserie och be sedan hen sammanfatta vad texten handlar om. </span></li></ol>'),
('Att sammanfatta-muntligt','Läsa','<p class="c2"><span class="c3">Genom att sammanfatta en text kontrollerar du din egen f&ouml;rst&aring;else av texten. Det inneb&auml;r att du plockar ut det viktigaste ur texten och formulerar det med egna ord.</span></p><p class="c2 c7"><span class="c3"></span></p><p class="c2"><span class="c3">S&aring; h&auml;r kan du g&ouml;ra:</span></p><ol class="c10 lst-kix_w0yilpexd9f0-0 start" start="1"><li class="c0"><span class="c9">Stryk under nyckelord som oftast &auml;r substantiv och verb, anv&auml;nd g&auml;rna till&auml;gget </span><span class="c5">Texthelp Study Skills</span><span class="c3">&nbsp;f&ouml;r att f&auml;rgmarkera nyckelorden i googledokument.</span></li><li class="c0"><span class="c3">Dela upp texten i olika avsnitt</span></li><li class="c0"><span class="c3">G&ouml;r en tankekarta eller en lista med nyckelorden och viktiga begrepp</span></li><li class="c0"><span class="c3">L&auml;gg ifr&aring;n dig ursprungstexten och spela in dig sj&auml;lv n&auml;r du muntligt sammanfattar texten. Anv&auml;nd tankekartan f&ouml;r att komma ih&aring;g alla delar.</span></li></ol>'),
('Klocka din läsning','Läsa','<p class="c0"><span class="c2">Det tar olika l&aring;ng tid att l&auml;sa olika typer av texter. Om du vet att du ska l&auml;sa en bok som du ska redovisa kan det vara bra att ta reda p&aring; hur l&aring;ng tid det tar att l&auml;sa den boken.</span></p><p class="c0"><span class="c2">G&ouml;r s&aring; h&auml;r:</span></p><p class="c0"><span class="c2">Ta fram en klocka. </span></p><p class="c0"><span class="c2">V&auml;lj en sida ur boken.</span></p><p class="c0"><span class="c2">Starta klockan.</span></p><p class="c0"><span class="c2">L&auml;s h&ouml;gt precis s&aring; snabbt att du f&ouml;rst&aring;r vad du l&auml;ser.</span></p><p class="c0"><span class="c2">Stoppa klockan.</span></p><p class="c0 c1"><span class="c2"></span></p><p class="c0"><span class="c2">Om du nu vet att det tog dig exempelvis 2,5 min att l&auml;sa sidan. &nbsp;S&aring; kan du titta hur m&aring;nga sidor boken har. &nbsp;Sen &auml;r det bara att r&auml;kna.</span></p><p class="c0"><span class="c2">S&auml;g att boken har 100 sidor. D&aring; vet du att det tar 250 min att l&auml;sa boken. </span></p><p class="c0"><span class="c2">Det betyder att du beh&ouml;ver &auml;gna dig &aring;t ungef&auml;r 4 timmar och 20 minuters l&auml;sning.</span></p><p class="c0 c1"><span class="c2"></span></p><p class="c0"><span class="c2">Nu &auml;r det dags f&ouml;r bakl&auml;ngesplanering. Om boken ska vara f&auml;rdigl&auml;st om 10 dagar hur mycket beh&ouml;ver du d&aring; l&auml;sa varje dag?</span></p><p class="c0"><span class="c2">G&ouml;r en plan och skriv in i loggboken!</span></p><p class="c0 c1"><span class="c2"></span></p><p class="c0"><span class="c2">(r&auml;tt svar: 25 min om dagen)</span></p><p class="c0 c1"><span class="c2"></span></p><p class="c0"><span class="c2">Kom ih&aring;g att olika texter tar olika l&aring;ng tid att l&auml;sa.</span></p>'),
('Komma igång','Läsa','<p class="c1"><span class="c0">Innan du b&ouml;rjar l&auml;sa din text, vare sig det &auml;r en bok eller en text i ett &auml;mne, s&aring; &auml;r det bra att skaffa dig en snabb &ouml;verblick &ouml;ver inneh&aring;llet. Dels f&ouml;r att &ouml;ka p&aring; din f&ouml;rf&ouml;rst&aring;else och dels f&ouml;r att du snabbare kommer in i texten.</span></p><p class="c1 c3"><span class="c0"></span></p><p class="c1"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c1 c3"><span class="c0"></span></p><ol class="c4 lst-kix_ri7ddo132yqu-0 start" start="1"><li class="c1 c2"><span class="c0">Om du ska b&ouml;rja l&auml;sa en ny bok s&aring; l&auml;s f&ouml;rst baksidestexten, d&aring; f&aring;r en snabb inblick i vad boken handlar om. </span></li><li class="c1 c2"><span class="c0">Ska du l&auml;sa och plugga in faktatext i en bok eller p&aring; en webbsida s&aring; b&ouml;rja med att l&auml;sa rubrikerna och titta p&aring; bilderna.</span></li><li class="c1 c2"><span class="c0">Ofta har bilderna bra bildtexter som ger dig en bra f&ouml;rst&aring;else f&ouml;r vad texten handlar om. </span></li><li class="c1 c2"><span class="c0">Att l&auml;sa en inneh&aring;llsf&ouml;rteckning ger dig ocks&aring; en god &ouml;verblick av vad boken eller texten handlar om.</span></li></ol>'),
('Målstegen','Läsa','<p class="c0"><span>Ibland kan du beh&ouml;va dela upp skolarbetet och t&auml;nka dig v&auml;gen mot m&aring;let som en stege. Genom att g&ouml;ra korta delm&aring;l s&aring; blir det enklare att steg f&ouml;r steg n&aring; m&aring;let.</span></p><p class="c0 c2"><span class="c1"></span></p><p class="c0"><span class="c1">Bild/filmruta/ljudfil</span></p><p class="c0 c2"><span class="c1"></span></p><ol class="c7 lst-kix_q267hqk1m49v-0 start" start="1"><li class="c0 c4"><span class="c1">Dela upp texten, glosorna, uppgifterna i hanterbara delm&aring;l</span></li><li class="c0 c4"><span class="c1">L&auml;s och l&auml;r in f&ouml;rsta delm&aring;let</span></li><li class="c0 c4"><span class="c1">Ta nu n&auml;sta steg i din m&aring;lstege och jobba med n&auml;sta delm&aring;l samtidigt som du repeterar f&ouml;rsta delm&aring;let</span></li><li class="c0 c4"><span class="c1">Forts&auml;tt tills du n&aring;tt slutm&aring;let</span></li></ol>'),
('Skapa inre bilder','Läsa','<p class="c4"><span class="c0">Att skapa inre bilder kan underl&auml;tta din f&ouml;rst&aring;else f&ouml;r det du l&auml;ser. Genom att anv&auml;nda dina sinnen blir det l&auml;ttare f&ouml;r dig att s&auml;tta dig in i texten, du blir kreativ, nyfiken och vill veta mer.</span></p><p class="c3"><span class="c0"></span></p><p class="c3"><span class="c0"></span></p><p class="c4"><span class="c0">Bild/filmruta/ljudfil</span></p><p class="c3"><span class="c0"></span></p><p class="c3"><span class="c0"></span></p><p class="c4"><span class="c0">N&auml;r du l&auml;ser din text s&aring; anv&auml;nd alla dina sinnen f&ouml;r att skapa en inre bild</span></p><ul class="c2 lst-kix_nsrlwlj3l210-0 start"><li class="c1"><span class="c0">Vad ser du? </span></li></ul><p class="c4"><span class="c0">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Milj&ouml;n, personerna, f&auml;rgerna...</span></p><ul class="c2 lst-kix_nff7hp3qfm84-0 start"><li class="c1"><span class="c0">Vad h&ouml;r du?</span></li></ul><p class="c4"><span class="c0">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;R&ouml;ster, musik, oljud...</span></p><ul class="c2 lst-kix_je6cy22kiwk7-0 start"><li class="c1"><span class="c0">K&auml;nner du n&aring;gon doft?</span></li></ul><p class="c4 c7"><span class="c0">Mat, parfym, stank...</span></p><ul class="c2 lst-kix_k1gooafvsz5c-0 start"><li class="c1"><span class="c0">Om n&aring;gon &auml;ter/dricker n&aring;got, kan du k&auml;nna n&aring;gon smak?</span></li></ul><p class="c4 c7"><span class="c0">Choklad, pizza, &auml;pple...</span></p><ul class="c2 lst-kix_n786bcvpht5d-0 start"><li class="c1"><span class="c0">Hur k&auml;nns det att vara d&auml;r?</span></li></ul><p class="c4 c7"><span class="c0">R&auml;dsla, gl&auml;dje, sorg...</span></p>'),
('Smartare läsning','Läsa','<p class="c1"><span class="c0">L&auml;ser du din text flera g&aring;nger men lyckas inte l&auml;ra dig det du l&auml;ser? </span></p><p class="c1"><span class="c0">Ingen fara!</span></p><p class="c1"><span class="c0">Den h&auml;r strategin kommer hj&auml;lpa dig och l&auml;ra dig att l&auml;sa smartare.</span></p><p class="c1 c3"><span class="c0"></span></p><p class="c1"><span class="c0">G&ouml;r s&aring; h&auml;r:</span></p><ol class="c5 lst-kix_kkzn5smc4pe-0 start" start="1"><li class="c1 c2"><span class="c0">N&auml;r du s&auml;tter dig och l&auml;ser, best&auml;m dig f&ouml;r hur mycket du ska l&auml;sa. Ett kapitel? 5 sidor?</span></li><li class="c1 c2"><span class="c0">L&auml;s sedan i tre niv&aring;er:</span></li></ol><ol class="c5 lst-kix_kkzn5smc4pe-1 start" start="1"><li class="c1 c4"><span class="c0">P&aring; f&ouml;rsta niv&aring;n l&auml;ser du det f&ouml;rsta och sista stycket, summeringar och rubriker.</span></li><li class="c1 c4"><span class="c0">P&aring; andra niv&aring;n l&auml;ser du hela texten. Du l&auml;ser noga, &auml;r avslappnad och koncentrerad.</span></li><li class="c1 c4"><span class="c0">P&aring; tredje niv&aring;n l&auml;ser du igenom alla mark&ouml;rer, tex kursiverade ord, bilder, bildtexter, illustrationer mm.</span></li></ol><ol class="c5 lst-kix_kkzn5smc4pe-0" start="3"><li class="c1 c2"><span class="c0">G&ouml;r inga anteckningar under tiden du l&auml;ser, bara l&auml;s. Du kan g&ouml;ra markeringar i texten med en f&auml;rgpenna, men anteckna inte under tiden du l&auml;ser.</span></li><li class="c1 c2"><span class="c0">Ta en paus p&aring; 5 minuter.</span></li><li class="c1 c2"><span class="c0">Avsluta med att skriva ner anteckningar om det du l&auml;ste.</span></li></ol>'),
('Ställa frågor','Läsa','<p class="c4"><span class="c5">F&ouml;r att f&ouml;rst&aring; texten kan det vara bra att st&auml;lla fr&aring;gor. Du kan ska st&auml;lla fr&aring;gorna till dig sj&auml;lv och kan med f&ouml;rdel jobba i par/grupp.</span></p><p class="c0"><span class="c5"></span></p><p class="c4"><span class="c5">L&auml;s texten och skriv ner fr&aring;gor till dig sj&auml;lv eller till en studiekompis</span></p><p class="c4"><span class="c5">Ungef&auml;r s&aring; h&auml;r:</span></p><p class="c0"><span class="c5"></span></p><p class="c4"><span class="c5">TEXT:</span></p><h3 class="c11" id="h.1igcqd9r3jfg"><span class="c3">Medveten andning</span></h3><p class="c4"><span class="c2">Det som &auml;r s&aring; fantastiskt med v&aring;r andning &auml;r att den &auml;r st&auml;ndigt n&auml;rvarande. Den kan inte vara n&aring;gon annanstans. Utan att m&auml;rkas h&aring;ller den oss vid liv. G&aring;rdagens andning kan inte hj&auml;lpa oss idag och morgondagens andning g&auml;ller inte just nu. </span></p><p class="c4"><span class="c2">Att stanna upp och bli medveten om andningen &auml;r ett s&auml;tt att landa i nuet. </span></p><p class="c4"><span class="c2">F&ouml;r att ta in en kort paus i vardagen kan du g&ouml;ra s&aring; h&auml;r:</span></p><p class="c4"><span class="c2">R&auml;kna till ett p&aring; inandning och till ett p&aring; utandning.</span></p><p class="c4"><span class="c2">R&auml;kna till tv&aring; p&aring; inandning och tv&aring; p&aring; utandning.</span></p><p class="c4"><span class="c2">R&auml;kna till tre p&aring; inandning och tre p&aring; utandning och s&aring; vidare, och s&aring; vidare tills du kommer till tio. </span></p><p class="c4"><span class="c2">Om tankarna b&ouml;rjar vandra konstaterar du bara att de finns d&auml;r och &aring;terv&auml;nder sedan till att andas. F&ouml;rs&ouml;k att b&ouml;rja om fr&aring;n b&ouml;rjan.</span></p><p class="c4"><span class="c2">Kommer du &auml;nda till tio?</span></p><p class="c0"><span class="c7"></span></p><p class="c0"><span class="c5"></span></p><ul class="c9 lst-kix_r0txskvel5ke-0 start"><li class="c4 c8"><span class="c5">Du kan g&ouml;ra om de olika rubrikerna till fr&aring;gor</span></li><li class="c4 c8"><span class="c5">Konstruera en fr&aring;ga d&auml;r du m&aring;ste h&auml;mta information fr&aring;n flera delar av texten</span></li><li class="c4 c8"><span class="c5">St&auml;ll fr&aring;gor kring bilder i texten om det finns s&aring;dana</span></li></ul><p class="c0"><span class="c5"></span></p><p class="c4"><span class="c5">Ungef&auml;r s&aring; h&auml;r:</span></p><p class="c4"><span class="c5">Vad &auml;r medveten andning?</span></p><p class="c4"><span class="c5">Vad &auml;r speciellt med andningen?</span></p><p class="c4"><span class="c5">Hur kan vi tr&auml;na medveten andning?</span></p><p class="c0"><span class="c5"></span></p><p class="c0"><span class="c5"></span></p><p class="c4"><span class="c5">D&auml;refter st&auml;ller ni era fr&aring;gor till varandra, j&auml;mf&ouml;r fr&aring;gorna och vilka svar de olika fr&aring;gorna ger.</span></p>'),
('Översiktsläsa','Läsa','<p class="c3"><span class="c0">N&auml;r du &auml;r p&aring; jakt efter anv&auml;ndbar information fr&aring;n flera olika k&auml;llor, eller vill f&aring; &ouml;verblick &ouml;ver en bok, text, n&auml;tsida eller tidning s&aring; &auml;r det bra att anv&auml;nda sig av metoden &ouml;versiktsl&auml;sning.</span></p><p class="c3 c4"><span class="c0"></span></p><p class="c3 c4"><span class="c0"></span></p><ul class="c5 lst-kix_e4vekdpn0ynv-0 start"><li class="c1"><span class="c0">L&auml;s igenom inneh&aring;llsf&ouml;rteckningen (om det finns en).</span></li><li class="c1"><span class="c0">L&auml;s rubriker och underrubriker.</span></li><li class="c1"><span class="c0">Titta p&aring; bilderna och l&auml;s bildtexterna.</span></li><li class="c1"><span class="c0">L&auml;s den f&ouml;rsta meningen i varje stycke (beskriver ofta inneh&aring;llet i hela avsnittet).</span></li><li class="c1"><span class="c0">L&auml;s den sista meningen i varje stycke (inneh&aring;ller ofta en sammanfattning eller en slutsats).</span></li><li class="c1"><span class="c0">L&auml;s sammanfattningar.</span></li><li class="c1"><span class="c0">Leta nyckelord (substantiv, verb och adjektiv).</span></li><li class="c1"><span class="c0">Leta namn och siffror.</span></li><li class="c1"><span class="c0">Leta kursiverade ord.<br></span></li></ul>'),
('Analysera uppgiften','Planera ditt lärande','<p class="c3"><span class="c0">N&auml;r du b&ouml;rjar p&aring; en ny uppgift kan det vara en bra id&eacute; att skapa sig &ouml;verblick &ouml;ver hur mycket tid du kommer att beh&ouml;va l&auml;gga p&aring; den och hur mycket hj&auml;lp du beh&ouml;ver.</span></p><p class="c3 c6"><span class="c0"></span></p><p class="c3"><span class="c0">Bild&hellip;</span></p><p class="c3 c6"><span class="c0"></span></p><p class="c3"><span class="c0">Instruktion till strategin:</span></p><p class="c3"><span>L&auml;s igenom uppgift och f&auml;rgkoda de olika delarna i den.</span></p><p class="c3 c6"><span class="c0"></span></p><p class="c3"><span class="c4">Markera med gr&ouml;nt </span><span class="c0">de delar som du tycker verkar l&auml;tt</span></p><p class="c3"><span class="c5">Markera med gult </span><span class="c0">de delar som du tror att du kan men som kanske beh&ouml;ver f&ouml;rklaras</span></p><p class="c3"><span class="c7">Markera med r&ouml;tt </span><span class="c0">de delar som du vet du kommer beh&ouml;va k&auml;mpa med och d&auml;r du beh&ouml;ver hj&auml;lp.</span></p><p class="c3 c6"><span class="c0"></span></p><p class="c3"><span class="c0">Arbetsm&aring;l:</span></p><ul class="c1 lst-kix_mzc4oewxwjjs-0 start"><li class="c3 c8"><span class="c0">Att g&ouml;ra research fr&aring;n trov&auml;rdiga k&auml;llor</span></li><li class="c3 c8"><span class="c0">Presentera intressanta och intressev&auml;ckande fakta om en f&ouml;rfattare</span></li><li class="c3 c8"><span class="c0">Att visa hur text och bild kan komplettera varandra</span></li></ul><p class="c2"><span class="c0"></span></p><p class="c3"><span class="c0">Exempel:</span></p><p class="c3"><span class="c0">G&ouml;r en tankekarta om en k&auml;nd f&ouml;rfattare</span></p><ol class="c1 lst-kix_erqdfblaxcqh-0 start" start="1"><li class="c3 c8"><span class="c0 c4">v&auml;lj en f&ouml;rfattare du gillar</span></li><li class="c3 c8"><span class="c0 c4">g&ouml;r research om f&ouml;rfattaren</span></li><li class="c3 c8"><span class="c0 c7">v&auml;lj vilka sidor som &auml;r trov&auml;rdiga</span></li><li class="c3 c8"><span class="c0 c5">best&auml;m vad du tycker &auml;r intressantast</span></li><li class="c3 c8"><span class="c0 c5">g&ouml;r en skiss hur tankekartan ska se ut</span></li><li class="c3 c8"><span class="c0 c4">g&ouml;r tankekartan</span></li><li class="c3 c8"><span class="c0 c5">ange k&auml;llor b&aring;de f&ouml;r bild och text</span></li><li class="c3 c8"><span class="c0 c5">skriv ner din arbetsprocess och hur du gjorde dina val</span></li></ol>'),
